style.css:
@-moz-document url-prefix("http://di0zyw94wnben.cloudfront.net/lsver08092017343257432/courses/") {
    .cs-HTML {
        background: rgba(0, 0, 0, 1) !important;
    }
    .screen {
        background-color: #090909 !important;
    }
}

@-moz-document url-prefix("https://ulfirefightersafety.org/resources.html") {
    html,
    body {
        background-color: #090909 !important;
    }
    .resources__filter-bar {
        background-color: #252525 !important;
    }
    .resources__filter-bar__left__element {
        color: #d9d9d9 !important;
    }
    .resources__filter-bar__left__element--active {
        background-color: #3f3f3f !important;
        border: 1px solid #2b2b2b !important;
    }
.btn--outline {
    background-color: #3f3f3f !important;
    box-shadow: inset 0 0 0 2px #2b2b2b  !important;
    color: #d9d9d9  !important;
}
}
