style.css:
@-moz-document url-prefix("https://members.iracing.com/jforum") {
body {
    background: #202225!important;
    color: #b9babc;
}
a {
    color: #b9babc;
}
tr {
    background: #36393e;
}
#contentHeader {
    background: #36393e;
    border-left: 1px #4f545c solid;
    border-right: 1px #4f545c solid;
    border-top: 1px #4f545c solid;
    border-radius: 6px 6px 0px 0px;
}
#contentBody {
    background: #36393e;
    border-left: 1px #4f545c solid;
    border-right: 1px #4f545c solid;
    border-bottom: 1px #4f545c solid;
    border-radius: 0px 0px 6px 6px;
}
#contentFooter
/* Bottom */

{
    background: #202225;
}
.contentBar {
    background: #282a2e;
    border: 1px #4f545c solid;
    border-radius: 6px;
}
.postBody
/* Text post */

{
    color: #b9babc;
}
.postBody a
/* Link color */

{
    color: #56b6d5;
}
.trPosts {
    background: #36393e;
}
#forum {
    border: 1px solid #4f545c;
}
.trDark {
    border: 1px solid #4f545c;
    background-color: #282a2e;
}
#topCorners {
    background: none;
}
#bottomCorners {
    background: none;
}
.trTopBlank {
    background: none;
}
.trBlank {
    background: none;
}
blockquote {
    color: #888a90;
    background-color: #2d2f33;
    border-left: 1px solid #888a90;
    border-top: 1px solid #888a90;
}
.post {
    color: #b9babc;
    background: #2d2f33;
    border: 1px solid #4f545c;
    border-radius: 6px;
}
.message {
    color: #b9babc;
    background: #2d2f33;
    border: 1px solid #4f545c;
    border-radius: 6px;
}
.tdTopic,
.tdAuthor,
.tdMeta,
.tdLast,
.tdPostAuthor {
    border-left: 1px solid #4f545c;
    border-right: 1px solid #4f545c;
}
.trTop {
    border-left: 1px solid #4f545c;
    border-right: 1px solid #4f545c;
}
.userSignature {
    color: #888a90;
    border-top: 1px solid #4f545c;
}
img[src*="iconQuote"],
img[src*="iconProfile"],
img[src*="iconEmail"],
img[src*="iconIgnore"],
img[src*="iconAim"],
img[src*="iconYahooInstantMessenger"],
img[src*="iconMsnInstantMessenger"],
img[src*="iconIcq"],
img[src*="latestReply"],
img[src*="iconClip"],
img[src*="iconPoll"],
img[src*="iconWebsite"],
img[src*="iconEdit"] {
    filter: invert(75%);
}
.trFade td {
    border-right: 1px solid #4f545c!important;
}
a.homeLink {
    background: none;
    padding: 3px 4px 0 7px;
}
tr,
#trTop,
.thTopMid,
.tdCategory,
#trPoll td {
    background: none;
}
#trTop,
#trPoll,
.tdCategory {
    border: 1px solid #4f545c;
    padding-top: 2px;
}
.trTopBlank strong {
    color: #b9babc!important;
}
.tdIcon,
.tdTopic,
.tdAuthor,
.tdMeta,
.tdLast,
.tdCheck,
.tdForumName,
.tdTopicShort {
    border: 1px solid #4f545c;
}
input[name="helpbox"] {
    background-color: #36393e;
}
#poll tr td {
    background-color: #36393e;
}
a#home {
    background-position: 0 0;
}
a#memberSite {
    background-position: -123px 0;
}
a#recentTopics {
    background-position: -244px 0;
}
a#hotTopics {
    background-position: -365px 0;
}
a#profile {
    background-position: -486px 0;
}
a#messages {
    background-position: -607px 0;
}
a#search {
    background-position: -728px 0;
}
a#logOut {
    background-position: -849px 0;
}
a:hover#home {
    background-position: 0 20px;
}
a:hover#memberSite {
    background-position: -123px 20px;
}
a:hover#recentTopics {
    background-position: -244px 20px;
}
a:hover#hotTopics {
    background-position: -365px 20px;
}
a:hover#profile {
    background-position: -486px 20px;
}
a:hover#messages {
    background-position: -607px 20px;
}
a:hover#search {
    background-position: -728px 20px;
}
a:hover#logOut {
    background-position: -849px 20px;
}
.contentBarButton {
    background: none;
    text-indent: 0;
    text-align: center;
    line-height: 20px;
    margin-top: -1px;
    border: 1px solid #4f545c;
}
#postANewTopic:after {
    content: 'New Topic';
    line-height: 20px;
}
#messageInboxUnread:after {
    content: ' Unread Messages';
    line-height: 20px;
}
#sentMessages:after {
    content: 'Sent Messages';
    line-height: 20px;
}
#messageInboxUnread {
    font-size: 12px;
    text-indent: 0;
    background: none;
    padding: 0;
    line-height: 20px;
    height: 20px;
    margin-right: -1px;
}
.contentBar,
.contentBarButton {
    height: 20px;
}
#writeAMessage,
#previousPage,
#postANewTopic,
#replyToTheTopic {
    border-top-left-radius: 6px;
    border-bottom-left-radius: 6px;
    margin-left: -1px;
}
#viewNewTopics,
#markAllAsRead {
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
    margin-right: -1px;
}
.thTopMid {
    text-shadow: none;
    color: #b9babc;
    border: 1px solid #4f545c;
}
.subContent {
    width: 650px;
}
.trDark td {
    border: 1px solid #4f545c;
}
img[src*="staff2"] {
    width: 120px;
    height: 36px;
    box-sizing: border-box;
    padding-left: 120px;
    background: url(http://members.iracing.com/jforum/images/avatar/staff.png) left top no-repeat;
}
#newMessages {
    margin: 5px 0 0 571px;
    padding: 5px 0 0 0;
    background: none;
    background-color: #36393e;
    border-radius: 10px;
    border: 1px solid #4f545c;
}

#stopWatching:after {
    content: "Stop Watching";
}
#watchThisTopic:after {
    content: "Watch Topic";
}

#previewMessage a, .tdMessage a
{
    color: #56b6d5;
}

#previewMessage a:hover, .tdMessage a:hover
{
    text-decoration:underline
}
}
