style.css:
@-moz-document url-prefix("http://www.netvibes.com") {
  #topLinks
  {
    display: none;
  }

  .nv-pager
  {
    display: none;
  }

  .nv-feedList .item
  {
    font-size: 1.0em;
  }

  .moduleContent
  {
    padding: 2px;
  }

  .gripper
  {
    width: 0.3%;
  }
    
  /*  .flexilayout-4-0 #column01, .flexilayout-4-0 #column02, .flexilayout-4-0 #column03, .flexilayout-4-0 #column04 {
    	width: 24.70%;
    }*/
    
  .flexilayout-4-0 #column01, .flexilayout-4-0 #column02, .flexilayout-4-0 #column03, .flexilayout-4-0 #column04
  {
    width: 24.70%;
  }
    

    /* Required after UI change of 20 12 2016: */
  .nv-condensed-item-list-renderer.white-shadows
  {
    border-top: 1px solid #fff;
    padding-top: 1px;
  }

  .nv-item-list-renderer.white-shadows
  {
    border-bottom: 1px solid #fff;
    padding-bottom: 1px;
  }

  .nv-condensed-item-renderer .item-header
  {
    padding: 2px 8px;
  }
    
    
    /*element {
       height: 100px;
    }*/
    /*.rssreader-container >*/ 
  .nv-item-list-renderer-reader
  {
    height: 100px;
  }

}
