style.css:
@-moz-document domain("twitter.com"){
  
/**                          **/
/** Dark Mode V1.0.4 by VooDoo **/
/**                          **/


.global-nav-inner {
    background: #212121;
}

body{
    color: #ccc;
    background-color: #161616;
}

.nav>li, .ProfileCardStats-statLabel, .account-group .username, .time, .time a, .created-via, .import-prompt p>span, .Footer-item, .Footer-link, .global-nav .search-icon .Icon, .Trends .context-trend-item .trend-item-stats, .flex-module-header h3, a.account-group:hover .username, a.account-group:focus .username, a.account-summary:focus .username, .ProfileTweet-actionButton, .ProfileTweet-actionButtonUndo, .ProfileTweet-actionCount, .ReplyingToContextBelowAuthor, .EdgeButton--primary, .tweet-context, .tweet .context .pretty-link s, .tweet .context .pretty-link b, .tweet>.context .with-icn .Icon, .btn-link.translate-button, .btn-link.translate-button .Icon--translator, .EmojiPicker-trigger .Icon, .EmojiPicker-clearQuery, .account-summary .dismiss, .tweet-form.condensed div.rich-editor.is-showPlaceholder:before, .tweet-form.condensed div.rich-editor:empty:before, .tweet-form .reply-users, .stats a, .metadata, .metadata button.btn-link, .metadata a, .modal-close .Icon--close, .tweet .time, .AdaptiveFiltersBar-target, .SearchExtrasDropdown-toggle, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink .name, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:hover .name, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:focus .name, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:active .name, .DashUserDropdown.dropdown-menu li .Icon, .ProfileHeaderCard-birthdate, .ProfileHeaderCard-joinDate, .ProfileHeaderCard-location, .ProfileHeaderCard-onlineHours, .ProfileHeaderCard-responsivenessLevel, .ProfileHeaderCard-url, .ProfileHeaderCard-vineProfile, .ProfileHeaderCard-periscopeProfile, .ProfileHeaderCard-birthdateText, .ProfileHeaderCard-joinDateText, .ProfileHeaderCard-locationText, .ProfileHeaderCard-onlineHoursText, .ProfileHeaderCard-responsivenessLevelText, .ProfileHeaderCard-urlText, .ProfileHeaderCard-vineProfileText, .ProfileHeaderCard-periscopeProfileText, .ProfileHeaderCard-screennameLink, .ProfileHeaderCard-screennameLink:hover, .PhotoRail-heading, .ProfileNav-item.is-active .ProfileNav-label, .ProfileNav-item.is-active:hover .ProfileNav-label, .ProfileNav-value, .ProfileNav-label, .EdgeButton--tertiary, .dropdown-menu li>a, .dropdown-menu .dropdown-link, .translation-attribution, .TwitterCard[data-theme=night-mode] .SummaryCard-destination, .ProfileCard .ProfileCard-screennameLink, .ProfileCard .ProfileCard-screennameLink:hover, .ProfileCard .ProfileCard-screennameLink:focus, .stream-container .grid-tweet .grid-tweet-actions .Icon, .Icon, .DMInboxItem-snippet, .DMInboxItem-timestamp, .tweet-form.has-attribution .attribution, .GridTimeline-emptyText, .ListCreationModule-description, .ProfileListItem-ownerName, .ProfileListItem-memberCount, .TwitterCard .PollXChoice-footer, .ActivityItem-activityTimestamp {
    color: #ccc;
}
.global-nav .search-input {
    background-color: #424242;
    border-radius: 21px;
    border: 1px solid #505050;
}
.global-nav .search-input:focus, .global-nav .search-input.focus {
    background-color: #232323;
}

.DashboardProfileCard, .Trends, .module .flex-module {
    border: 1px solid #404040;
    background: #212121;
}
.DashboardProfileCard-avatarImage {
    border: 2px solid #525252;
}
.DashboardProfileCard-avatarLink {
    background-color: #1b1b1b;
}
.stream-item:not(.no-header-background-module) {
    background: #222222;
    border-left: 1px solid #414141;
    border-right: 1px solid #414141;
}
.tweet {
    border-bottom: 1px solid #414141;
    cursor: pointer;
}
.tweet:hover {
    background-color: #080808;
}
.new-tweets-bar {
    background-color: #525252;
    border-top: 1px solid #414141;
    border-bottom: 1px solid #414141;
}
.new-tweets-bar:hover {
    background-color: #222222;
    border: 1px solid #191919;
}
.wtf-module .import-prompt .service {
    border: 1px solid #222222;
    background: #3c3c3c;
    color: #000;
}
.home-tweet-box, .LiveVideo-tweetBox, .RetweetDialog-commentBox {
    background-color: #222222;
}
.top-timeline-tweetbox .timeline-tweet-box {
    border: 1px solid #414141;
}
input, textarea, div[contenteditable], .t1-select {
    background-color: #434343;
}
.EdgeButton--secondary {
    background-color: #151515;
}
.EdgeButton--secondary:hover, .EdgeButton--secondary:active {
    background-color: #0c0c0c;
}
.EmojiPicker-categoryColumn {
    border-right: 1px solid #414141;
}
.dropdown-menu {
    background: #222222;
}
.EmojiPicker-categoryHeader {
    background-color: #424242;
    border-bottom: 1px solid #414141;
    color: #ccc;
}
.EmojiPicker-query {
    background-color: #222222;
    border-bottom: 1px solid #414141;
}
  li.stream-item.highlighted {
    background-color:  #424242 !important;
}
.conversation-module .conversation-tweet-item .tweet:hover {
    background-color: #171717;
}
.QuoteTweet:hover, .QuoteTweet:active {
    border-color: #414141;
}
.ActivityItem:not(.has-clickAction) .QuoteTweet--slim:hover {
    background: #0a0a0a;
}
.QuoteTweet {
    border: solid 1px #414141;
}
.ThreadedConversation, .ThreadedConversation--loneTweet {
    background-color: #222222;
}
.permalink-tweet:hover, .permalink-tweet.focus {
    background-color: #191919;
}
.permalink-tweet, .permalink-tweet:hover {
    background-color: #353535;
}
.permalink .inline-reply-tweetbox {
    background-color: #222222;
    border-top: 1px solid #424242;
}
.permalink {
    background-color: #1b1b1b;
}
.content-header, .content-no-header {
    border: 1px solid #414141;
}
.NotificationsHeadingContent {
    background-color: #0e0e0e;
    border-bottom: 1px solid #424242;
}
.modal-content, .modal .modal-tweet {
    background-color: #222222;
}
.modal-header {
    background: #0a0a0a;
    border-bottom: 1px solid #424242;
}
.SidebarFilterModule {
    background-color: #222222;
    border: 1px solid #424242;
}
.AdaptiveFiltersBar {
    background-color: #222222;
}
.DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:hover, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:focus, .DashUserDropdown.dropdown-menu .DashUserDropdown-userInfoLink:active {
    background-color: #353535!important;
}
.dropdown-caret .caret-inner {
    border-bottom-color: #222222;
}
.dropdown-divider {
    border-bottom: 1px solid #414141;
}
.EdgeButton--tertiary {
    background-color: #161616;
    border: 1px solid #414141;
}
.EdgeButton--tertiary:hover {
    background-color: #121212;
    color: #ccc;
}
.wtf-module .UserSmallListItem {
    border-bottom: 1px solid #414141;
}
.ProfileSidebar .TweetImpressionsModule, .ProfileSidebar .ProfileLifelineInfo, .ProfileSidebar .RelatedUsers {
    background-color: #222222;
    border: 1px solid #424242;
}
.ProfileHeading-content {
    background-color: #0a0a0a;
    border: 1px solid #424242;
}
.TwitterCard[data-theme=night-mode] .TwitterCard-container--clickable:hover {
    border-color: #ccc;
}
#global-tweet-dialog .modal-tweet .tweet:hover, #global-tweet-dialog .modal-tweet .tweet {
    background: #212121;
}
.ProfileCanopy-navBar {
    background-color: #0a0a0a;
}
.ProfileNav-item--userActions {
    background-color: #0a0a0a;
}
.TwitterCard[data-theme=night-mode] .TwitterCard-container {
    border-color: #5a5a5a;
}
.stream-end-inner {
    background-color: #0a0a0a;
}
.stream-end-item, .stream-end, .stream-loading, .stream-placeholder {
    border: 1px solid #424242;
}
.ProfileAvatar {
    background: #0a0a0a;
    border: 5px solid #151515;
}
.ProfileCard {
    background-color: #222222;
    border: 1px solid #414141;
}
.AdaptiveRelatedSearches {
    background-color: #222222;
    border: 1px solid #424242;
}
.ProfileCard-avatarLink {
    background-color: #101010;
}
.stream-container .AdaptiveStreamGridImage .grid-tweet-actions-container {
    background: #222222;
    border: 1px solid #414141;
}
.Gallery-media {
    background: none;
}

/* MP */
.DMActivity-header {
    background-color: #0a0a0a;
    border-bottom: 1px solid #424242;
}
.DMInboxItem {
    border-bottom: 1px solid #414141;
}
.DMInboxItem {
    background: #222222;
}
.DMInboxItem:hover {
    background-color: #080808!important;
}
.DMActivity-body {
    background-color: #161616;
}
.DirectMessage--received .DirectMessage-text {
    background-color: #353535;
    color: #ccc;
}
.DirectMessage--sent .DirectMessage-text {
    background-color: #9e9e9e;
    color: #fff!important;
}
.DirectMessage-footer {
    color: #ccc
}
.u-bgUserColorLightest {
    background-color: #0a0a0a !important;
}
.DMInboxItem.is-unread {
    background-color: #0a0a0a;
}
.TokenizedMultiselect-inputContainer {
    border: 1px solid #f3f3f3;
}
.DMTokenizedMultiselectSuggestion.is-highlighted {
    background-color: #222222;
    border-bottom: 1px solid #424242;
    border-top: 1px solid #424242;
}
.InputToken {
    background: #4a4a4a;
}
.InputToken:focus {
    background-color: #222222;
    border-color: #424242;
    color: #424242;
}
.DMButtonBar {
    background-color: #0a0a0a;
}
.DMNotice {
    background: #0a0a0a;
}

/* GIF popup */
.FoundMediaSearch-query {
    background-color: #161616;
    border: 1px solid #424242;
}
.FoundMediaSearch-query .Icon--search {
	color: #ccc
}
.tweet-form .thumbnail-container {
    background-color: #0a0a0a;
    box-shadow: inset 0 1px 0 #424242;
}
.ComposerThumbnail-overlay {
    background: #222222;
    color: #14171a;
}

/* Liste */
.ProfileListsTimeline .GridTimeline-end:not(.has-items) {
    background-color: #222222;
    border: 1px solid #424242;
}
.ListCreationModule {
    background-color: #222222;
    border: 1px solid #424242;
}
.ProfileListItem {
    background-color: #222222;
    border-bottom: 1px solid #424242;
}
.content-header .header-inner {
    background-color: #0a0a0a;
    border-bottom: 1px solid #424242;
}
.dashboard .stats {
    background-color: #222222;
}
.follow-card .follow-bar {
    border: 1px solid #414141;
    box-shadow: inset 0 1px 0 #424242;
    background: #1b1b1b;
}
.module li:first-child .list-link {
    border-top: 1px solid #424242;
}
.module .list-link:hover, .module .active .list-link {
    background-color: #222222;
    color: #ccc;
}
.module .list-link {
    background-color: #222222;
    color: #ccc;
    border: 1px solid #424242;
}
/* Poll */
.TwitterCard .PollXChoice-optionContainer .PollXChoice-choice--radio {
    border: 1px solid #ccc;
}
.TwitterCard .EdgeButton--secondary {
    border: 1px solid #ffffff;
    color: #ffffff;
}
.TwitterCard[data-theme=night-mode] .PollXChoice-choice--chart {
    background-color: #929292;
}

/* Add another Tweet */
.self-thread-permalink-cta {
    background-color: #252525;
}
    
    
    
    
    
/*	Scroll Bar        	*/
*::-webkit-scrollbar{
    width:14px !important;
}
*::-webkit-scrollbar-thumb{
    min-height:35px !important;
    background: #424242 !important;
    border: none !important;
    border-radius: 0px !important;
}
*::-webkit-scrollbar-track-piece{
    background-color: #212121 !important;
    border: none !important;
}
/* Copyright */
li.Footer-item.Footer-copyright.copyright:after {
  content: " | Dark Style by @DearVooDoo"
}
}
