style.css:
::-webkit-scrollbar {
	width: 3px;
	height: 3px;
}
 
::-webkit-scrollbar-track,
::-webkit-scrollbar-track-piece {
	background: rgba(0,0,0,0);
}
 
::-webkit-scrollbar-thumb {
    border-radius: 5px;
    -webkit-box-shadow: inset 0 0 300px rgba(100,100,100, .8); 
}
::-webkit-scrollbar-corner,
::-webkit-scrollbar-button {
	display: none;
}
