style.css:
@-moz-document url-prefix("http://www.365djs.com/share.html") {
.nav.nav_light,.nav_menu,.side_bar,.copyright.copyright_second {
    display:none
}
html {
    margin-top: 15%;
}
.article_inner {
    background: #fff;
}
.article h2 {
    height: 60px;
    line-height: 60px;
    font-size: 30px;
}
.countdown_share_number {
    width: 370px;
    margin: auto;
}
.article_content {
    width:100% !important;
    float:none !important;
    box-sizing: border-box;
}
.countdown_share_number span.cn {
    font-size: 20px;
    position: relative;
    top: -19px;
}
}
