body.all-styles-hidden-by-filters:after {
    top: 9rem;
    left: 4rem; }

/* search and sort */
#search-wrapper {
    display: none; }

#filters:not([open]) {
    margin-bottom: 66px; }

#sort-wrapper {
    display: none !important; }

/* menu - upside  redet */
#stylus-manage #header > p {
    margin: 0 !important; }

/* Check all styles for updates */
#stylus-manage #update-check {
    position: absolute;
    top: 95px;
    left: 300px;
    width: 220px; }

#stylus-manage #update-all {
    margin: 0;
    width: 100%; }

#apply-all-updates {
    margin-top: 1em;
    height: auto;
    width: 100%;
    padding-right: 300px; }

#update-all-no-updates {
    display: block;
    margin: 1em 0 0;
    padding: 8px 25px;
    border-radius: 4px;
    background: var(--bgc4);
    box-shadow: inset 0 0 0 1px var(--bgc3); }

#check-all-updates-force {
    margin-top: -6px;
    height: auto;
    width: 100%;
    background: linear-gradient(#f00 0%, #ac0000 100%); }

/* Write new styles */
#add-style-wrapper {
    display: block;
    position: absolute;
    top: 15px;
    left: 15px; }

#add-style-as-usercss-wrapper {
    display: inline-flex; }

/* Actions */
#actions summary {
    display: none; }

#manage-options-button {
    position: absolute;
    top: 95px;
    left: 520px; }

/* Options -manage- */
#stylus-manage #options {
    display: none;
    position: relative;
    column-count: 1;
    border-radius: 4px;
    background: var(--bgc4);
    padding: 0 1em 0.7em;
    max-height: 1em;
    min-height: 1em;
    overflow: hidden;
    transition-property: max-height, padding-top;
    transition-delay: 3s;
    transition-duration: 0.2s;
    box-shadow: inset 0 0 0 1px var(--bgc3); }

#stylus-manage #options:hover {
    padding: 3.2em 1em 0.7em;
    max-height: 150px;
    transition-delay: 0.5s; }

#stylus-manage #options:not([open]) {
    padding: 0 1em 0.7em;
    height: 1em;
    transition-delay: 0s; }

#stylus-manage #options summary {
    position: absolute;
    top: 0;
    left: 0;
    margin: 0;
    padding: 0.1em 1em 0.2em;
    max-width: initial;
    width: calc(100% - 2em);
    background: var(--bgc3);
    z-index: 1; }

#stylus-manage #header summary h2 {
    margin: 0.1em 0 0;
    font-size: 14px;
    border: none; }

#stylus-manage #options #newUIoptions > * {
    display: flex !important; }

#stylus-manage #options #options-buttons {
    position: absolute;
    top: 28px;
    left: 284px;
    margin: 0; }

#faviconsHelp {
    width: 100%; }

/* backup */
#backup {
    position: absolute;
    top: 15px;
    left: 300px; }

#backup summary {
    display: none; }

#backup #backup-message {
    display: none; }

#backup > p {
    margin: 0;
    width: 230px; }

#backup #backup-buttons {
    flex-wrap: nowrap; }

/* get styles */
#manage-text {
    position: absolute;
    top: 60px;
    left: 300px;
    padding: 0; }

/* installed list */
#stylus-manage #installed {
    display: block !important;
    position: absolute;
    top: 60px;
    padding-left: 0;
    padding-bottom: 10px;
    margin: 0 !important;
    width: 100%;
    height: calc(100vh - 60px);
    overflow-y: scroll; }

/* balance of width : style-name and applies-to */
.newUI .style-name {
    width: 60%; }

.newUI .target {
    max-width: calc(40vw - var(--actions-width)) !important; } }

/* Installed Styles ======== Less than 550px ============ */
@media screen and (max-width: 550px) {
#stylus-manage {
    overflow: hidden; } } }

@-moz-document regexp("chrome-extension://.*"),
regexp("moz-extension://.*") {
