style.css:
@-moz-document domain("faz.net") {
/* ==========================================================================
   VARIABLES
   ========================================================================== */

:root
{
  --color-container-light-3: rgb(255, 255, 255);
  --color-container-dark-0: rgb(90, 90, 90);
  --color-container-dark-1: rgb(70, 70, 70);
  --color-container-dark-2: rgba(60, 60, 60, 1);
  --color-container-dark-2-transparent: rgba(60, 60, 60, 0);
  --color-container-dark-3: rgba(50, 50, 50, 1);
  --color-container-dark-3-translucent: rgba(50, 50, 50, 0.5);
  --color-container-dark-3-transparent: rgba(50, 50, 50, 0);
  --color-container-dark-4: rgb(30, 30, 30);
  --color-container-dark-4-translucent: rgba(30, 30, 30, 0.5);
  --color-container-dark-5: rgb(20, 20, 20);
  --color-text-light-1: rgb(180, 180, 180);
  --color-text-light-2: rgb(230, 230, 230);
  --color-text-light-3: rgb(255, 255, 255);
  --filter-blur-1: blur(2px);
  --filter-blur-2: blur(5px);
}

/* ==========================================================================
   FONTSMOOTHING
   ========================================================================== */

html,
html *
{
    -webkit-font-smoothing: antialiased !important;
    font-smoothing: antialiased !important;
}

/* ==========================================================================
   CONTAINER
   ========================================================================== */

body
{
    background-color: var(--color-container-dark-1);
}

.tsr-Comment,
.gh-Wrapper,
.cna-P2,
.cna-P4,
.cna-P5,
#PBAd,
#PB2Ad,
.cna-P7,
#FAZContentLeft,
#FAZContentRight,
.Artikel.wide-main-content,
.billboard,
.ContentFooter,
#FAZContent,
.tsr-Compact
{
 	background-color: var(--color-container-dark-2);
}

.gf-Wrapper,
.gf-Wrapper_Inner
{
	background-color: var(--color-container-dark-3);
}

.gh-MainNav
{
	margin: 0;
	padding-left: 1.25rem;
    padding-right: 1.25rem;
}

.ctn-PlaceholderContent_Description-has-text-overline,
.atc-Intro,
.ctn-PlaceholderContent_Description,
.tsr-More,
.gh-MainNav_RootLink,
.gh-MainNav_AuxLink
{
    background-color: var(--color-container-dark-2);
}

.tsr-Base-has-border,
.ctn-PageFunctions,
.gh-MainNav,
.SucheKopf,
.Redaktion #FAZContentRight, .Suche #FAZContentRight,
.SuchergebnisKopf,
.SortierFunktion,
.FilterBoxKopf,
.FilterBox .Last,
li.FilterLink,
a.FilterArrowLink
{
    border-color: var(--color-container-dark-0);
}

.lbl-Base-has-bar:after,
.tsr-Base_ListShortLinksItem:before
{
	background-color: var(--color-container-light-3);
}

.tsr-Base_Headline-lg:after,
.tsr-Base_Headline-xl:after,
.tsr-Base_ContentWrapperInner:after,
.tsr-Base_Headline:after
{
    background-color: var(--color-container-dark-0);
}

.tsr-Top1_TeasersWrapper,
.tsr-LeadWide_TextWrapperOuter,
.tsr-Lead_TextWrapperOuter,
.o-ModuleWrapper,
.ctn-PlaceholderContent,
.ctn-PlaceholderBox,
.sld-TeaserCommentColumn-has-background,
.tsr-Base_TextWrapper,
.tsr-Topic
{
	background-color: var(--color-container-dark-2);
    border-color: var(--color-container-dark-0);
}

.tsr-Top1_TeasersWrapper:after,
.tsr-Top1_TeasersWrapper:before
{
	background-color: var(--color-container-dark-2);
}

div.vwd_we_faz_c004 div.kursticker,
div.vwd_we_faz_c004 div.kursLink,
.VWDcontent,
.VWDcontent.extern,
div.vwd_we_faz_c004 div.contentBox,
div.vwd_we_faz_c004 div.kursticker .inner::after
{
   	background-color: var(--color-container-dark-2) !important;
}

.js-cna
{
    background-color: var(--color-container-dark-2);
}

/* ==========================================================================
   TYPO
   ========================================================================== */

.article-reportage p,
.atc-Text,
.tsr-Base_Content
{
    color: var(--color-text-light-2);
}

.atc-Meta,
.atc-MetaAuthorText
{
    color: var(--color-text-light-1);
}

.atc-MetaAuthorLink,
.tsr-Base_ContentAuthorNames,
.cbx-Author_NameLink,
.SucheTitel,
.Teaser620 span.Headline,
.FilterBox a.ResetLink,
.FilterBox .FilterBoxRow span.FilterLabel,
.atc-Text a,
.sld-TeaserCommentColumn_NavigationLink
{
    color: var(--color-text-light-3);
}

.tsr-Base_HeadlineEmphasis,
.atc-HeadlineEmphasis,
.atc-MetaAuthorLink,
.frm-Comments_Login,
.frm-Comments_Login *,
.MultimediaNavigation ul.RessortListe li a,
.PagerNav,
.nvg-Breadcrumb_Item,
.sld-TeaserCompact_ItemCounter,
.tbl-Favorite_TableHeadColumn,
.KommentarBoxTeaser p.Copy,
.PulldownVersandTeaser p.Copy,
.Teaser p.Copy,
.Teaser210 p.Copy,
.Teaser300 p.Copy,
.Teaser380 p.Copy,
.Teaser440 p.Copy,
.Teaser620 p.Copy,
.TeaserInRondell p.Copy,
.TeaserMultimedia ol,
.TeaserMultimedia ul,
.FilterSpaltenTitel,
.FilterLabel,
.ArchivBox,
.FinSearchBox,
.ArchivBox a,
.FinSearchBox a,
.tsr-Base_ListShortLinksItem
{
	color: var(--color-text-light-2);
}

.tsr-Base_HeadlineText,
.lbl-Base,
.tsr-Base_Category,
.gf-Topics_ItemLink,
.gh-MainNav_SectionsLink,
.atc-HeadlineText,
.nvg-Paginator_Item,
.atc-IntroText,
.btn-Link .btn-Base_Text,
.faz-html-h5,
.gh-MainNav_ArticleTitle,
.lst-LinksTopics_TopicsListLink,
.VWDcontent *,
div.vwd_we_faz a,
.Rubrikenkopf,
.TeaserInRondell span.Headline,
.MultimediaNavigation ul.RessortListe li a.Selected,
.PagerNav .Current, .PagerNav a:hover,
.FilterBoxKopf,
a.FilterArrowLink,
span.Trefferzahl,
.mmContainer_header .headline a,
.dpa_games
{
    color: var(--color-text-light-3);
}

.gh-LogoStage_Caption,
.ctn-PlaceholderContent_Description-has-text-overline .ctn-PlaceholderContent_DescriptionHeadlineText,
.TeaserInRondell span.Stichwort,
.TeaserInfo,
.TeaserInfo span.Date
{
	color: var(--color-text-light-1);
}

.gh-LogoStage_Date,
.gh-LogoStage_MetaNavDropdownLink,
.gh-LogoStage_MetaNavLink,
.cbx-Author_PersonalInfo,
.ctn-PlaceholderContent_DescriptionHeadlineEmphasis
{
	color: var(--color-text-light-1);
}

.gh-Footer_Main
{
	color: var(--color-text-light-3);
}


/* ==========================================================================
   WIDGETS
   ========================================================================== */

.o-Icon-logo-faz,
.btn-Base_Faz,
.lay-Base_Close,
.gh-IconSearchBig
{
	filter: brightness(1000000%)
}

.gh-IconLogoFazShortInner,
.icon-page_finanzen_next,
.icon-paper_last,
.lst-Services_Item,
.icon-search,
input.SubmitBtn,
.icon-arrow_up_black
{
	filter: invert(100%);
}

.nvg-Breadcrumb_List:after
{
	background-image: linear-gradient(90deg, var(--color-container-dark-2-transparent), var(--color-container-dark-2) 80%);
}

.gh-MainNav_Article:after
{
	background-image: none;
}

.lst-Comments_SorterFormCriteriaOption .switcher.default
{
    border-color: var(--color-container-dark-0);
    background-color: var(--color-container-dark-4);
}

.lst-Comments_SorterFormCriteriaOption .switcher.default.is-active
{
    background-color: var(--color-container-dark-0);
}

.lst-Comments_SorterFormCriteriaOption .switcher.default .slider
{
    background-color: var(--color-container-dark-2);
    -webkit-box-shadow: none;
	box-shadow: none;
    border: none;
}

.js-global-header-main-nav-static
{
   	background-color: var(--color-container-dark-3);
}

.gh-MainNav-is-sticky
{
    background-color: var(--color-container-dark-3-translucent);
    -webkit-backdrop-filter: var(--filter-blur-2);
    backdrop-filter: var(--filter-blur-2);
}

.lay-Search
{
    background-color: var(--color-container-dark-4-translucent);
    -webkit-backdrop-filter: var(--filter-blur-2);
    backdrop-filter: var(--filter-blur-2);
}

/* ==========================================================================
   COMMENTS
   ========================================================================== */

.lst-Comments_CommentInfoDateText,
.lst-Comments_CommentInfoNameText
{
    color: var(--color-text-light-1);
}

.lst-Comments_CommentText
{
    color: var(--color-text-light-2);
}

.lst-Comments_CommentTitle,
.lst-Comments_CommentInteractionLink,
.lst-Comments_CommentReplyItem,
.lay-Search_Label
{
    color: var(--color-text-light-3);
}

.lst-Comments_CommentBox
{
    background-color: var(--color-container-dark-3);
    border-color: var(--color-container-dark-0);
}

.lst-Comments_Item-level2:before,
.lst-Comments_Item-level3:before,
.lst-Comments_Item-level4:before,
.lst-Comments_Item-level2 .lst-Comments_CommentBox:before,
.lst-Comments_Item-level3 .lst-Comments_CommentBox:before,
.lst-Comments_Item-level4 .lst-Comments_CommentBox:before,
.lst-Comments_Item-level2 .lst-Comments_CommentImageContainer:before,
.lst-Comments_Item-level3 .lst-Comments_CommentImageContainer:before,
.lst-Comments_Item-level4 .lst-Comments_CommentImageContainer:before
{
	background-color: var(--color-container-dark-0);
}

/* ==========================================================================
   ANIMATIONS
   ========================================================================== */

.o-Js-is-disabled .tsr-Base:hover .tsr-Base_Image,
.tsr-Base-is-hovered .tsr-Base_Image-has-hover-effect
{
    transform: scale(1.2) rotate(0.1deg) translateZ(0);
    transition: transform 0.5s ease-in-out;
}
}
