style.css:
@-moz-document domain("kanban-chi.appspot.com") {
.bottombar__right-corner {
    display: none;
}
.topbar {
    background-color: transparent;
}
.topbar__collaborators {
    background-color: transparent;
    border: none;
}
.page--theme-24 {
    background-image : url(https://i.imgur.com/YZjJXsO.jpg); /* hubble image */
    background-position : 50% 50%; /* center background image (optional) */
    background-color : #222;
}
.page--theme-decorated .list__title {
    color: white;
}
.ncard {
    background-color: rgba(0,0,0,0.7);
}
.ncard-title {
    color: #eee;
}
.list__counter {
    color: #eee;
}
.list:hover .list__counter,
.card-indicators,
.ncard-tags__link,
.ncard-tags__unit {
    color: #aaa;
}
}
