style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(avclub.com),
               domain(deadspin.com), 
               domain(gawker.com),
               domain(gizmodo.com),
               domain(io9.com),
               domain(jalopnik.com), 
               domain(jezebel.com), 
               domain(kinja.com),
               domain(kotaku.com),
               domain(lifehacker.com)
{
    .instream-native-video.instream-native-video--desktop.instream-permalink.initialized,
    ASIDE.branded-item,
    SECTION .recent-video,
    .instream-native-video--frontpage,
    .flex-video {
        display: none !Important;}
}
