style.css:
@-moz-document domain("www.flowdock.com") {
  .bubble {
    border-left-width: 5px;
    border-left-style: solid;
    padding-left: 5px;
    padding-right: 5px;                    
  }

  .bubble:hover {
    background-color: lightgrey;
  }

  .thread-avatar {
    position: relative;
    z-index: 2;
  }
}
