style.css:
@-moz-document url-prefix(https://wallet.webmoney) {

body,
.top-panel,
.top-search__text,
.send-menu-loader progress--cover progress--cover-white,
::-webkit-scrollbar, ::-webkit-scrollbar-track,
::-webkit-scrollbar-track-piece {
	background-color: #222 !important;
}

.wm-menu,
.item-block,
.keeper-nav,
.form-footer,
.modal__body>*,
.info-line__label,
.info-line__value,
.modal-popup__body,
::-webkit-scrollbar-corner,
::-webkit-scrollbar-thumb {
	background-color: gray !important;
}

.title-bar,
.ctrl__item,
.header-line,
.button--white {
	background-color: darkgray !important;
}

.scrollable:after,
td.header-user-block a.user-link-to-events:after {
	background: linear-gradient(180deg, transparent 0, #222) !important;
}

.scrollable:before {
	background: none;
}

.item-block__date {
    box-shadow: -3px 0 5px 3px gray !important;
}

::-webkit-scrollbar {
	width: 10px;
}

}
