style.css:
@-moz-document regexp('https://github.com/.+/.*/pull/.*/files.*') {
  .container {
    width: 90%;
  }
}

@-moz-document regexp('https://github.com/.+/.+/commit/.+') {
  .container {
    width: 90%;
  }
}

@-moz-document regexp('https://github.com/.+/.+/blob/.+') {
  .container {
    width: 90%;
  }
}

@-moz-document regexp('https://github.com/.+/.+/compare/.+') {
  .container {
    width: 90%;
  }
}

@-moz-document regexp('https://gist.github.com/.+/.+') {
  .container {
    width: 90%;
  }
}
