style.css:
@-moz-document domain("diforfree.org") {


html {
	height: 100%;
    width: 100%;
}

body {
	height: auto;
    width: auto;
    position: absolute;
    left: 5px;
    top: 5px;
    right: 5px;
    bottom: 5px;
    margin: 0;
}

.bd-jplayer-playlist {
	margin: 0 auto;
}

.bd-jplayer-playlist .jp-playlist ul {
	max-height: none;
}

.bd-jplayer-playlist, .bd-jplayer-playlist .jplayer_container, #jp_container_1 {
	height: 100%;
	position: static;
}
#jp_container_1 {
	position: relative;
}
.jp-playlist {
    position: absolute;
    top: 160px;
    bottom: 0;
    left:0;
    right:0;
/*     height: 100%;
    max-height: 100%; */
	overflow-y: scroll;
}
}
