style.css:
@-moz-document domain("web.telegram.org") {
.tg_head_wrap, .tg_head_split,

.im_page_wrap,

.im_message_wrap,

.im_send_panel_wrap, .im_send_form
{
  max-width: initial;
}

.tg_head_split,
.im_page_split
{
	display: flex;
}

.tg_head_logo_wrap,
.im_dialogs_col_wrap
{
	max-width: 30em;
  float: none;
}

.tg_head_main_wrap,
.im_history_col_wrap
{
  float: none;
	flex-grow: 1;
}

.composer_emoji_tooltip
{
  left: auto !important;
  right: 1rem;
}

.composer_emoji_tooltip_tail
{
  margin-left: 40px;
}
}
