style.css:
@-moz-document domain('pizzasandcoke.wordpress.com') {
    /*[[themecolor]]*/

    
  	body div#page{
		max-width: 950px !important;
       width: 100% !important;
       margin: 20px auto;
        padding: 30px 90px;
    }
body div#page div#primary, body div#page div#secondary {
    float: none !important;
    width: 100% !important;
    margin: 0 !important;
}
  body div#page div#secondary {
    	text-align: center;
  }
  body div#page div#primary #main{
      margin: 0 !important;
  }
  body div#page div#primary #main .entry-content p{
  		font-size:  /*[[fontsize]]*/ !important;
    	margin-bottom: 1.3em;
  }
}

