style.css:
@-moz-document domain(roblox.com) {

.slide-item-container-left, .profile-avatar-right {
    background: #598bac;
    box-shadow: 0px -1px 4px 0px black;
    z-index: 1;
}
 
#header {
    background-color: #0180d0;
    box-shadow: 0 0 3px black;
}

a:link, a:visited, a:active {
    color: #191919;
}

div#GroupsPeoplePane_Members {
    background-color: #0c70b2;
    border-top: 1px #0c70b2;
    box-shadow: 1px 1px 6px black;
}

.navigation .notification-icon,
.rbx-highlight
{
  background: #7B241C;
}

.SearchBox input, .content .SearchKeyword {
    padding: 2px;
    padding-left: 10px;
    border: 1px solid #0c70b2;
    background-color: #0c70b2;
}

.redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner, .tab.active, .tab-container>.tab-active, .tab-container>.tab-active:hover {
    background-color: #0c70b2;
    margin-top: 0;
    padding: 9px 7px;
    border-bottom: 0;
    position: relative;
    border-color: #0c70b2;
    z-index: 1;
    margin: 0 1px 0 0;
    top: 1px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}

.redesign .tab_white_31h_container .ajax__tab_inner, .tab, .tab-container>div {
    float: left;
    background-color: #0c70b2;
    padding: 7px;
    border: 1px solid #3da0e2;
    font-weight: bold;
    font-size: 15px;
    margin: 4px 2px 0 1px;
    border-bottom-width: 0;
    position: relative;
    top: -1px;
}

#GroupTitle div {
    color: #191919;
}

#chat-header
{
  background: #3da0e2;  /*Party header*/
}

.footer-link .text-footer-nav.privacy {
    color: #191919;
}

#SearchControls {
    height: 28px;
    clear: both;
    display: block;
    background: #3da0e2;
    border: 0px solid #ccc;
    margin: 0 0 15px 0;
    position: relative;
    padding: 2px 0;
}

.message-summary-content {
    color: #191919;
    margin: 3px 0 0;
}

.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
{
  box-shadow: 0 -4px 0 .6 #145A32 inset; /* Yea idk what this is for*/
}

.MyRank {
    width: 100%;
    font-size: 14px;
    color: #f2f2f2;
    margin-top: 10px;
}

.notification-stream-body {
    background-color: #0c70b2;
    position: relative;
    height: 420px;
    max-height: 420px;
    z-index: 10;
}

.GroupOwner {
    color: #f2f2f2;
    width: 100%;
    text-align: center;
    margin-top: 10px;
}

.input-group-btn .input-addon-btn {
    background-color: #0a70b2;
}

.notification-stream-header {
    background-color: #3da0e2;
    border-bottom: 1px solid #B8B8B8;
    padding: 0;
    position: relative;
    z-index: 11;
}

.notification-stream-list .notification-stream-item {
    -webkit-transition: right .8s ease;
    -o-transition: right .8s ease;
    transition: right .8s ease;
    background-color: #3da0e275;
    border-bottom: 1px solid #E3E3E3;
    height: 66px;
    width: 100%;
    padding: 9px;
    position: relative;
}

.notification-stream-list .notification-stream-item.unInteracted {
    background-color: #0c70b2;
}

.notification-stream-list .notification-stream-item.unInteracted .notification-display-text:after {
    background-color: #0c70b2;
}

.notification-data-container .notification-display-text .message-preview {
    color: #02b757;
    display: inline-block;
    word-break: break-all;
}

.notification-stream-header .text-link {
    float: right;
    padding: 7px 12px;
    color: #191919;
}

.rbx-game-server-item .rbx-game-status, .rbx-friends-game-server-item .rbx-game-status, .rbx-vip-server-item .rbx-game-status {
    color: #191919;
}

.para-overflow-toggle:after {
    background-color: #3ea0e2;
    content: "...";
    height: 24;
    position: absolute;
    right: 0;
    bottom: 0;
    width: 80px;
}

.messages .messageDivider {
    overflow: hidden;
    position: relative;
    border-top: 0px solid #191919;
    background: #3da0e2;
    padding: 9px 15px;
}

.table-header .first {
    border-left: 1px solid #3da0e2;
}

.forum-content-background, .forum-table-row {
    background-color: #3da0e2;
}

.section-content {
    background-color: #3da0e2;
    box-shadow: 0 1px 4px 0 rgba(25,25,25,0.3);
    padding: 15px;
    position: relative;
}

.para-overflow:after {
    background-color: #3da0e2;
    content: "...";
    position: absolute;
    right: 0;
    bottom: 0;
}

.slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #3da0e2;
    padding: 20px 30px;
    height: 300px;
    float: right;
    width: 50%;
}

.text-label {
    color: #f2f2f2;
    font-weight: 400;
}

.form-control {
    display: block;
    width: 100%;
    background-color: #0c70b2;
    background-image: none;
    border: 1px solid #B8B8B8;
    border-radius: 3px;
}

.rbx-select-group .rbx-select {
    border-radius: 3px;
    margin: 0;
    padding: 0;
    min-width: 105px;
    width: 100%;
    height: 38px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    line-height: 18px;
}

.container-footer {
    background-color: #3da0e2;
    padding: 0px 0;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus {
    color: #555;
    background-color: #5cc4ff;
    border: 1px solid #ddd;
    border-bottom-color: transparent;
    cursor: default;
}

.rbx-tab .rbx-tab-heading {
    -webkit-transition: all,0.2s,ease-in-out;
    -o-transition: all,0.2s,ease-in-out;
    transition: all,0.2s,ease-in-out;
    padding: 12px 2%;
    background-color: #3da0e2;
    border: 0 none;
    border-radius: 0;
    line-height: 100%;
    box-shadow: 0 none;
}

.btn-control-sm {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #0c70b2;
    border-color: #0c53b2;
    color: #191919;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 7px 7px;
    font-size: 16px;
    line-height: 100%;
    border-radius: 3px;
}

.text-link, .message-detail a, .text-link:link, .message-detail a:link, .text-link:hover, .message-detail a:hover, .text-link:visited, .message-detail a:visited, .text-link:active, .message-detail a:active {
    color: #f2f2f2;
}

.text-date-hint {
    font-size: 12px;
    font-weight: 400;
    color: #ddd;
}

a.ad img {
    display: block;
    border: none;
    box-shadow: -2px -1px 7px black;
}
  
.message-detail {
    background: #3da0e2;
    padding: 15px;
    position: relative;
    margin: 9px 0;
    word-break: break-word;
}

.game-card-container {
    background-color: #3da0e2;
    position: relative;
    box-shadow: 0 1px 4px 0 rgba(25, 25, 25, 0.55);
    border-radius: 3px;
    float: left;
    height: 100%;
    margin: 0 auto;
    padding: 0;
    text-align: left;
    -webkit-text-fill-color: #ffffff;
    font: message-box;
    width: 100%;
}

.text-link, .vlist .list-item .list-body .list-content a, .text-link:link, .vlist .list-item .list-body .list-content a:link, .text-link:hover, .vlist .list-item .list-body .list-content a:hover, .text-link:visited, .vlist .list-item .list-body .list-content a:visited, .text-link:active, .vlist .list-item .list-body .list-content a:active {
    color: #e3e3e3;
}

input#ctl00_cphRoblox_GroupSearchBar_SearchButton {
    background-color: #0c70b2;
    border-color: #0c70b2;
    border-radius: 3px;
}

.game-card-experimental, .game-card-name-secondary {
    font-size: 12px;
    font-weight: 400;
    color: #f2f2f2;
}

#catalog-content {
    background-color: #3da0e2;
    border: 5px solid #3da0e2;
}

#navbar-search-input, #navbar-search-btn {
    border: 1px solid #0c70b2;
}

.c-version .search-options .menu-link:hover, .c-version .search-options .menu-link.active {
    color: #f2f2f2;
}

.text-footer-nav, .text-footer-nav:link, .text-footer-nav:visited, .text-footer-nav:active {
    color: #343434;
    font-size: 21px;
    font-weight: 300;
}

body {
    background-color: #E3E3E3;
    color: #191919;
    font-family: "Source Sans Pro",Arial,Helvetica,sans-serif;
    font-weight: 300;
    font-size: 16px;
    width: 100%;
    min-width: 320px;
    margin-bottom: 168px;
    overflow-y: scroll;
}

.scroller {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #3da0e2;
    border: 1px solid rgba(25, 25, 25, 0.1);
    width: 30px;
    height: 223px;
    zoom: 1;
    position: absolute;
    z-index: 1000;
    box-shadow: none;
    top: 0;
}

.text-footer {
    font-size: 12px;
    font-weight: 400;
    color: #333;
}

.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
{
  background: #3da0e2 !important; /*Forum colors and messaging underline color*/
}

.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs
{
  background-color: #5DADE2 !important;
  border-color: #053C66;   /*Updrade button*/
}

input.default {
    font-style: italic;
    color: #f2f2f2;
}

.input-field {
    border: 0px solid #B8B8B8;
    color: #f2f2f2;
    font-weight: 300;
    font-size: 16px;
    height: 38px;
    line-height: 100%;
    padding: 5px 12px;
    -webkit-appearance: none;
}

.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover
{
  background: #5DADE2 !important;
  border-color: #053C66;
}

#TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
{
  display: none !important;
}

body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("https://corp.roblox.com/wp-content/uploads/2016/10/Roblox_Feature_6.jpg") !important; /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
  }

#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
{
  background: linear-gradient(to bottom, #145A32, #145A32);  
}

#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
{
  display: none;
}

#GamesPageLeftColumn
{
  width: auto;
  margin-right: 10px !important;
  margin-left: 10px !important;
}

.text-pastname {
    font-size: 14px;
    font-weight: 400;
    color: #191919;
}

.text-name, .text-name:link, .text-name:hover, .text-name:visited, .text-name:active {
    color: #191919;
}

.text-report, .text-report:link, .text-report:hover, .text-report:visited, .text-report:active {
    font-size: 14px;
    font-weight: 400;
    color: #ec0000;
}

.StandardPanelContainer .StandardPanelWhite {
    background-color: #3da0e2;
    position: relative;
    left: -5px;
    top: -1px;
    width: 100%;
    padding: 6px;
    margin-bottom: -6px;
}

#Summary_tab .RobuxColumn, #Summary_tab .TicketsColumn {
    float: left;
    background-color: #3da0e2;
    margin: 0 3px;
}

.stack .stack-list .stack-row {
    background-color: #3da0e2;
    margin: 0 0 6px;
    padding: 12px;
    box-shadow: 0 1px 3px rgba(45, 45, 45, 0.96);
    position: relative;
}

.table-header th {
    font-weight: 600;
    background-color: #0c70b2;
}

.table td, .table th {
    padding: 5px;
    border-top: 1px solid #191919;
    margin: 0;
    text-align: left;
}

#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    max-width: 970px;
    background-color: #3da0e2;
    box-shadow: 0px 0px 3px 0px black;
}

#GamesListsContainer > div
{
  background-color: transparent !important;
  width: 100% !important;
}

#navigation {
    background-color: rgba( 12, 112, 178, 1) /*Left side navi*/
} 

.rbx-left-col li a {
    color: #ffffff;
    display: block;
}

.rbx-btn-primary-lg {
    background-color: rgba(130, 200, 255, 0.5);  
}

.rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus {
    background-color: rgba(66, 176, 244, 0.5);
} /* above effect the background of the catalog and the forums*/}
