style.css:
@-moz-document domain('drudgereport.com'),
url-prefix('http://drudgereport.com') {
    body {
        background-color: black;
        color: white;
    }
    a {
        color: white;
    }
    hr {
        border: 0px;
        height: 1px;
        background-color: #3b3b3b !important;
    }
    #app_col1 center table tbody tr td div {
        background-color: #3b3b3b !important;
    }
}
