style.css:
[role="main"] span, [role="banner"] span, textarea, [dir="auto"] {
  color: #eaeaea !important;
}

[role="button"][type="button"] div {
  color: black !important;
}

[role="main"], [role="main"] div {
  background-color: #213548 !important;
}

[role="banner"] div, [role="banner"] a {
  background-color: #21303f !important;
}

[role="article"] a > div, [aria-label^="Timeline"] > div > div > div {
  border-color: #324e62 !important;
}
