style.css:
@-moz-document regexp(".*\\.esa\\.io/.*") {
/* Base */
/*
    OPTIMIZE: If necessary, uncomment out for Code mirror

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@400;700&display=swap');
.editor-codemirror .CodeMirror { font-family: ui-monospace, SFMono-Regular, SF Mono, Menlo, Consolas, Liberation Mono, 'Noto Sans JP', monospace }
*/

:root {
    --color-link: #0a9b94;

    --color-border: rgba(0,0,0, .1);
    --color-border: #e5e5e5; /* Not transparent */
}
.user-theme--navy { --color-link: #0f6c96 }
.user-theme--sky { --color-link: #0491b7 }
.user-theme--grass { --color-link: #7daa02 }
.user-theme--rose { --color-link: #bc5053 }
}

@-moz-document regexp(".*\\.esa\\.io/.*") {
/* Hide Items

    - Post List
*/

/* - Post List */
:not(.pagination):not(.sorter) +
    .post-list .category-path li:first-child,
.post-list__left, /* Main Thumbnails */
.post-list .thumbnail-list.viewer-action__thumbnail-list,
.post-list__record /* Update Date */
{ display: none }
}

@-moz-document regexp(".*\\.esa\\.io/.*") {
/* Individuals

    - Posts index

    - Post List
    -- Separator

    - Post detail
    -- Comment
    -- TOC
    -- Markdown
    -- Code mirror

    - Revision, Diff detail
*/

/* - Posts index */
@media (min-width: 1101px) and (max-width: 1320px) {
    .posts-index .main-column { max-width: calc(100% - 220px - 45px) }
}

/* - Post List */
.post-list { margin-top: 1.5em }
.post-list__item {
    margin-top: .2em;
    border-bottom: 0;
    padding: .2em 0;
    line-height: 1;
    transition: .2s;
}
.post-list__item:focus { background: 0 }
.layout-home__center /*
    */ .post-list .post-title {
        float: left;
        margin: 0;
        margin-right: 1.2em;
        font-size: 120%;
    }
.post-list .post-title__link { color: var(--color-link) }
.post-list__footer { display: inline }
.post-list .viewer-action {
    float: right;
    box-shadow: 0 0 1px #0006;
    border-radius: 4px;
    padding: .3em .5em .2em 0;
    opacity: .4;
    transition: .2s;
}
.post-list .viewer-action:hover { opacity: 1 }
.post-list .viewer-action__item {
    margin: 0 0 0 10px;
    border-left: 1px solid rgba(0,0,0, .1);
    border-right: 0;
    padding-right: 0;
    padding-left: 12px;
}
.post-list .viewer-action__item:first-child {
    margin-left: 0;
    border-left: 0;
}
.post-list .viewer-action__item:empty { display: none }
.post-list__date,
.post-list__record { clear: both }
.post-list__content {
    margin: 1em 0;
    line-height: 1.8;
    transition: .2s;
}
.post-list .viewer-action__count {
    margin-top: auto;
    margin-bottom: auto;
    font-family: 'Helvetica Neue', Helvetica, snas-serif;
}

/* -- Separator */
* + .post-list__item { counter-increment: item-count }
.post-list__item:nth-child(5n + 6)::before {
    display: table;
    position: relative;
    margin: 1em auto -.5em;
    padding: 0 .5em;
    background: #fff;
    color: #eee;
    content: counter(item-count);
}
.post-list__item:nth-child(5n + 6) .post-list__inner { flex-wrap: wrap }
.post-list__item:nth-child(5n + 6) .post-list__inner::before {
    width: 100%;
    margin-bottom: 2em;
    border-top: solid 1px rgba(0,0,0, .05);
    content: "";
}

/* - Post detail */
.layout-post__content { justify-content: flex-end }
.layout-post__main {
    max-width: 48em;
    margin-right: unset;
}

/* -- Comment */
.layout-post__aside-content { max-width: calc(48em + 84px + 15px * 2) }

/* -- TOC */
.toc:empty { display: none }
.toc-list { margin-left: 1.65em }

/* -- Markdown
    
    --- Details
    --- Task List
*/
.markdown {
    --markdown-space-y: 1em;
    line-height: 1.6;
}
.markdown ul,
.markdown ol { padding-left: 1.75em }
.markdown li li { margin: 0 }
.markdown h4 { margin-top: 2em }

/* --- Details */
.markdown details {
    border: 1px solid var(--color-border);
    border-radius: 3px;
    padding: .5em 1em;
}
.markdown details:not(:first-child) { margin-top: var(--markdown-space-y) }
.markdown details:not(:last-child) { margin-bottom: var(--markdown-space-y) }
.markdown :where(li) > details { margin: .25em 0 }
.markdown details summary {
    margin: -.5em -1em;
    border-radius: 3px;
    padding: .5em 1em;
    background: rgba(0,0,0, .02);
    color: #586069;
    transition: border-radius .3s;
}
.markdown details summary:focus { background: rgba(0,0,0, .08) }
.markdown details summary:hover { background: var(--color-border) }
.markdown details[open] summary {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}
.markdown details[open] > summary:not(:last-child) {
    margin-bottom: 1.5em;
}

/* --- Task List */
.markdown li.task-list-item input {
    margin-top: -5px;
    vertical-align: middle;
}
.markdown li.task-list-item li { margin: .4em 0 }

/* -- Code mirror */
.cm-s-esa .cm-strong { font-weight: bold }
.cm-s-esa .cm-header-1 { line-height: 1.3 }

/* - Revision, Diff detail */
.revision-compare {
    display: table;
    position: static;
    margin: -1em 0 1em auto;
}
}
