@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mail.live.com") {



#RadAd_TodayPage_Banner {display:none !important;}

.SideAds {display:none !important; width:0px !important;}

#edtab {width:100% !important;}

.TodayOptOut BorderTop {width:100% !important;}

#CustComm_120x60 {display:none !important; width:0px !important;}

#RadAd_Skyscraper {display:none !important;}

#SkyscraperContent {display:none !important;}

#MainContent {right: 06px !important;}


}


/* Elimina le ADS in Live.com */

@-moz-document domain(home.live.com) {

#Ad300x250 {display:none !important;}

#Crm300x125 {display:none !important;}



}
