style.css:
@-moz-document domain("lichess.org")

  {
  body.dark
  {
    background-image: url("https://i.pinimg.com/originals/ce/7a/e6/ce7ae6251733e16e526fd5f0b4cb7a47.gif")  !important;
    color: #EEE;
  }

  .purple .is2d .cg-board, #top div.color_demo.purple
  {
    background-image: url("https://i.imgur.com/5obNVkp.png")  !important;
  }

  .lichess_board
  {
    padding: 35px;
    background-image: url("https://i.imgur.com/C8KnLSJ.png")  !important;
    background-size: cover;
                      /* <------ */
    background-repeat: no-repeat;
    background-position: center center;
  }

  .underboard
  {
    margin-top: 50px !important;
  }

  .ranks coord
  {
    margin-left: 2px !important;
  }

  .files coord
  {
    margin-top: 0px !important;
  }
 .cg-board square.last-move
  {
    background-color: rgba(49,210,71,0.5) !important;
}
}
