style.css:
@-moz-document url-prefix("http://chat.thecolorless.net") {
#header {
    top: 0;
    height: 125px;
    background: #000000; /* Header Bar bg color*/
    opacity: 0.8;        /* Header Bar transparency*/
    border-bottom: 1px solid #000;
    color: #111;
    text-shadow: #ccc 1px 1px 0;
}

body {
    background-image:url("https://i.imgur.com/0MynXiM.gif"); /*Background Image*/
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: 100% auto;
    color: #fff;
    text-shadow: #000 1px 1px 0;
    font-size: 12px;
    line-height: 18px;
    font-family: "Helvetica Neue", Helvetica, Arial, Verdana, sans-serif;
    overflow-y: scroll;
    overflow:visible;
}
#radio{
    opacity: 0.0;  /* Radio Widget Transparency */
}
}
