style.css:
@-moz-document url-prefix("https://www.qbittorrent.org/") {
html, body, #desktopHeader, #desktopNavbar, #mochaToolbar, #Filters {
    background: rgb(17,17,30) !important;
    color: #fff;
}

a {
 	color: rgb(30,160,210) !important;
}

#desktopNavbar li a {
    color: #fff !important;
    border-bottom-color: #1c2538 !important;
}

#desktopNavbar li a:hover {
    background: #1b2334 !important;
}

#desktopNavbar li ul {
    background: rgb(40,50,70) !important;
    background-image: none !important;
}

#desktopNavbar li ul .divider {
    border-top-color: #1a2130 !important;
}

#pageWrapper {
    border-top-color: #1c2538 !important;
}

.torrentTable, .dynamicTable {
    border-color: #1a2130 !important;
}

.torrentTable th, .dynamicTable th {
    background: rgb(50,50,65) !important;
    color: #fff !important;
}

.dynamicTable th {
    border-right-color: #1c2538 !important;
}

.torrentTable tr, .dynamicTable tr {
    background: #161b27 !important;
}

.torrentTable tr:nth-child(even), .dynamicTable tr:nth-child(even) {
    background: rgb(30,40,60) !important;
}

.torrentTable tr.selected, .torrentTable tr:hover, .dynamicTable tr.selected, .dynamicTable tr:hover {
    color: #fff !important;
}

.torrentTable tr.selected, .dynamicTable tr.selected {
    background: rgb(90,100,150) !important;
}

.torrentTable tr:hover, .dynamicTable tr:hover, ul.filterList li:hover {
    background: rgb(30,160,210) !important;
}

.toolbarTabs {
    padding-bottom: 0 !important;
}

.toolbarTabs li, .toolbarTabs li a {
    background-image: none !important;
    color: #fff !important;
}

.toolbarTabs li {
    margin-left: 2px !important;
    margin-right: 2px !important;
    margin-bottom: 0 !important;
    background: rgb(40,50,70) !important;
    border-radius: 2px 2px 0 0;
    box-shadow: 0 0 7px #111;
    background-position: left -70px
}

.toolbarTabs li.selected {
    background: rgb(70,80,100) !important;
    
}

.toolbarTabs li a {
    margin-left: 0 !important;
    padding: 4px 9px !important;
}

#propertiesPanel_header {
    height: 27px !important;
}

#propertiesPanel_headerContent {
    padding-top: 3px !important;
}

#prop_general fieldset, #watched_folders_tab tr, #watched_folders_tab th, #watched_folders_tab td {
    border-color: #999 !important;
}

#desktopFooter {
    background: rgb(40,50,70) !important;
    color: #fff !important;
}

.panel {
    background: #161b27 !important;
    border-bottom-color: #1c2538 !important;
    color: #fff !important;
}

.panel-header, .panel-headerContent, .toolbarTabs {
    background-image: none !important;
    background: #161b27 !important;
}

.panel-header {
    border-bottom-color: #1c2538 !important;
}

.horizontalHandle, .columnHandle {
    background: rgb(50,50,65) !important;
    border-color: rgb(50,50,65) !important;
}

.columnHandle:after {
    transform: rotate(90deg) !important;
}

.horizontalHandle {
    background-image: none !important;
}

.mochaCanvas {
    display: none !important;
}

.mochaTitlebar, .mochaTitlebar.replaced {
    background: #191e2d !important;
    border-radius: 7px 7px 0 0;
}

.mochaTitle {
    color: #fff !important;
}

.mochaToolbarWrapper {
    border-top-color: #aaa !important;
}

.mochaToolbar {
    border-top-color: #1c2538 !important;
    background: #161b27 !important;
    padding-top: 4px;
}

.mochaContentBorder {
    border-top-color: #1c2538 !important;
    border-bottom-color: #191e2d !important;
    border-bottom-width: 23px !important;
    border-radius: 0 0 7px 7px;
}

.mochaContentWrapper {
    background: #161b27 !important;
}

.mochaContent fieldset {
    border-color: #1c2538 !important;
}

.mocha::after {
    content: "";
    display: block;
    position: absolute;
    top: 4px;
    left: 5px;
    bottom: 7px;
    right: 5px;
    border-radius: 7px;
    box-shadow: 0 0 10px #000;
    z-index: -1000;
}

.mocha#preferencesPage::after {
    bottom: 6px;

}

.contextMenu, .contextMenu li ul {
    background: rgb(40,50,70) !important;
    border-color: #000 !important;
}

.contextMenu li a {
    color: #fff !important;
}

.contextMenu li a:hover {
    background: #1b2334 !important;
}

h2, h3 {
    color: #fff;
}

.filterList a {
    color: #FFF !important;
}

/*Makes the toolbar buttons, rightclick menu and menu bar white*/
.mochaToolButton, .contextMenu img, #desktopNavbar img, #addTrackersPlus{
    filter: grayscale(100%) brightness(5) !important;
}

/*Adjusts images in the footer and filter menu and torrents section (e.g. the downloading, seeding and paused icons) to be slightly brighter*/
#filtersColumn img, #transferList_wrapper img, #desktopFooter img{
    filter: brightness(1.5);
}

/* % of download completed*/
.progressbar_dark {
    background: #A00 !important;
}

/* % of download uncompleted*/
.progressbar_light {
    background: #FDD !important;
}

.panel-collapse {
    background: url(http:../theme/go-down) center center no-repeat;
    background-size: 24px;
}

select {
    background:  rgb(50,50,65);
    border-color: #000;
    color: #eee !important;
}

input, textarea {
	background: rgb(30,40,60) !important;
    color: #fff !important;
    border: 0px;
    white-space: nowrap;
}

input::placeholder {
    color: #aaa !important;
}

#downloadPage, #downloadPage_overlay, #downloadPage_contentWrapper, #downloadPage_iframe {
    height: 600px !important;    
}

#uploadPage, #uploadPage_overlay, #uploadPage_contentWrapper, #uploadPage_iframe {
    height: 420px !important;  
}

#error_div {
    color: #0f0;
}
}
