div#page-place.page-dashboard {background: none !important;}

#blocAFaire h3, #blocAnnonces h3 { background: url("https://cdn.discordapp.com/attachments/383674189560676352/390629135258615808/bordure2.png") repeat-x;
color: #2d1100; text-shadow: 0 0 2px #4f1703;}


#blocAnnonces {
  box-shadow: 0 0 3px #e1dbc7;
  float: left;
  width: 465px;
  margin: 8px 0;
  padding: 6px;
  box-sizing: border-box;
background: rgba(89, 0, 0, 0.5);
}
  #blocAnnonces h3 {
    font-size: 140%;
    padding: 6px 5px 6px;
    color: #2d1100; 
text-align: right;}

  #blocAnnonces a.annonce {
    display: block;
    padding: 7px 4px;
    border-bottom: 1px solid #d6eaff; }
    #blocAnnonces a.annonce:hover {
      background-color: rgba(255, 255, 255, 0.5);
      text-decoration: none; }
    #blocAnnonces a.annonce .date {
      background: #440d05;
      color: white;}
    #blocAnnonces a.annonce .intitule {
      color: #440d05;
      font-weight: bold;}
    #blocAnnonces a.annonce .intitule:hover {
      text-shadow: 0 0 4px #FFFFFF;}


#blocAFaire {
  margin: 8px 0 8px 11px;
  width: 450px;
  float: left;
  padding-top: 4px 0;
  box-sizing: border-box;
background: rgba(89, 0, 0, 0.5);
  box-shadow: 0 0 3px #e1dbc7; }
  #blocAFaire ul {
    font-weight: bold;
    color: #2d1100;
    background-color: #fff4e5;
}
  #blocAFaire li {
    color: #2d1100;
    list-style-type: none;
}
    #blocAFaire li.fait {
      color: #2d1100; }
  #blocAFaire .todo-list {
    color: #2d1100;
    background:none;}
  #blocAFaire a {
    color: #2d1100; }
    #blocAFaire a:hover {
      background: rgba(255, 255, 255, 0.5); }
  #blocAFaire a.done {
    color: #2d1100;
    background: rgba(155, 95, 0, 0.5);}
    #blocAFaire a.done .frequency {
      background: rgba(89, 0, 0, 0.5);}
  #blocAFaire .frequency {
    background: rgba(89, 0, 0, 0.5);}


  /*Pirika */
  .profile-calendar table td.ok span:hover {
    background-color: #C20A05;
    cursor: pointer;
}
.profile-calendar table td.ok span {
    display: block;
    background-color: #A50604;
    color: white;
    font-weight: bold;
    height: 100%;
    line-height: 200%;
}
.profile-calendar table .calendar-month {
    font-weight: bold;
    font-size: 15px;
    text-align: center;
    color: #DF7A21;
    font-family: Amaranth;
} /* Pirika */
  
  

