style.css:
@-moz-document domain("ecranlarge.com") {
/* Arriere plan 1 */
body {
    background-color: #1f1f1f;
    color: #fb6716;
}

/* Arriere plan 2 */
.content {
    background: #060606;
}

/* Textes Presentation Articles */
.articles article p {
    color: #fbfbfb;
}

/* article .date */
.articles article .date {
    color: #d1d1d1;
    background: #060606;
    font-size: 11px;
    padding: 2px 6px;
    display: inline;
}

/* Textes Articles */
section > .content-description,
section > p {
    color: #e6dada;
}
.sidebar article {
    background: #fdeee7;
    border-top: 2px solid #fdeee7;
    border-bottom: 2px solid #fdeee7;
}
.form-control::-moz-placeholder {
    color: #090909;
}
.form-control {
    background-color: #fdeee7;
}
footer .content-end {
    background: #fff0;
}
footer .content-end a {
    background: #dbdbdb00;
    color: #bfbcbc;
}
section.readers-critic .critic p {
    color: #e9e9e9;
}
section .date {
    color: #a2a2a2;
    background: #f3f3f300;
}
div.rich .date {
	color: #c0c0c0;
}

.h1 .small, .h1 small, .h2 .small, .h2 small, .h3 .small, .h3 small, .h4 .small, .h4 small, .h5 .small, .h5 small, .h6 .small, .h6 small, h1 .small, h1 small, h2 .small, h2 small, h3 .small, h3 small, h4 .small, h4 small, h5 .small, h5 small, h6 .small, h6 small {
	color: #e6dada;
}

.movie-page .articles article p {
	color: #e6dada;
}

.movie-page .articles article .date {
	color: #a2a2a2;
	background: #171717;
}

.movie-page .description .title {
	color: #e6dada;
}
}
