[page-subtype="playlist"] .ytd-playlist-video-list-renderer:hover>.ytd-playlist-video-renderer div#progress[style*="100%"]::before,
[page-subtype="channels"] .ytd-grid-video-renderer:hover div#progress[style*="100%"]::before,
