style.css:
@-moz-document url-prefix("https://mstdn.kemono-friends.info/") {

/*[[CW-style]]*/

/*[[scroll]]*/

/*[[unlisted]]*/

/*[[logout]]*/

/*[[hide-reaction]]*/

/* 画像を幅一杯表示 */
.media-gallery {
	height: auto !important;
	display: flex;
	justify-content: center;
	align-items: center;
	flex-wrap: wrap;
}
@media (min-width: 961px) {
	:not(.detailed-status) > .media-gallery {
		width: calc(100% + 64px);
		margin-top: 16px;
		position: relative;
		left: -64px;
	}
}
.media-gallery__item {
	flex-grow: 1;
	position: static;
	border: 1px solid rgba(128, 128, 128, .5);
}
.media-gallery__item-thumbnail img {
	max-height: calc(100vh / 3);
	object-fit: contain;
}
.media-gallery__item-gifv-thumbnail {
	object-fit: contain;
}
.media-gallery__gifv {
	height: 110px;
}
.detailed-status .media-gallery__gifv {
	height: auto;
}

/* 通知欄の表示を簡略化 */
.notification .display-name,
.notification .status__action-bar,
.notification .status__relative-time {
	display: none;
}
.notification__favourite-icon-wrapper + span {
	font-size: small;
}
.notification .status__content p {
	color: rgba(255, 255, 255, .75);
}

/* userIDを常に表示する */
.display-name__html {
	display: block;
	float: left;
	margin-right: .5rem;
}
.display-name__account {
	display: block;
	float: left;
}
/* clearfix */
.display-name:after {
	content: "";
	clear: both;
	display: block;
}

/* カラム幅を変更 */
@media (min-width: 631px) {
	.column {
		flex-grow: 1;
		width: 0;
	}
	.drawer,
	.column:last-of-type {
		flex-grow: 1;
		width: 0;
		max-width: 300px;
	}
/*[[drawer]]*/
/*[[drawer-right]]*/
}
/*[[compact]]*/
}

options.value:
/* CWを常に表示 */
.status__content--with-spoiler > .status__content__text {
	display: block !important;
	padding: .5rem;
	background-color: rgba(192, 192, 192, .25);
	border-left: 5px solid rgba(255, 255, 255, .75);
	max-height: 50vh;
	overflow: auto;
}
.detailed-status .status__content__text {
	max-height: none;
}

/* レイアウト調整 */
.status__content--with-spoiler > p:first-of-type {
	margin-bottom: 1rem !important;
}
.status__content--with-spoiler > div {
	display: none;
}

/* スクロールバーを見えなくする */
/* 十分に隠れない場合は "+ xx px" の所を適宜修正してください */
.scrollable {
	width: calc(100% + 23px);
}

/* スクロールバーを見えなくする */
.scrollable::-webkit-scrollbar {
	display: none;
}

/* 未収載・非公開投稿を見やすく (アイデア元： ブルーレヰ (@BlueRayi@mstdn.kemono-friends.info) さん） */
.status-unlisted,
.status-private {
	background: linear-gradient( 0deg, rgba(255, 255, 255, .25) 0%, rgba(0, 0, 0, .75) .5rem, rgba(0, 0, 0, 0) 100%);
}

/* ログアウトボタンを隠す(誤爆対策) */
.drawer__tab[aria-label="ログアウト"],
[href="/auth/sign_out"] {
	display: none;
}

.status__action-bar {
	position: absolute;
	bottom: 0;
	right: 0px;
	padding: 5px;
	opacity: 0;
	background-color: rgba(0, 0, 0, .5);
	box-shadow: 0px 0px 10px 0px rgba(255, 255, 255, .5);
	transition: all .25s ease .25s;
}
.status:hover .status__action-bar {
	opacity: 1;
}

.drawer {
	flex-grow: 0;
	opacity: 0;
	transition: opacity .15s linear .1s;
}
.columns-area:hover > .drawer{
	flex-grow: 1;
	opacity: 1;
}

@media (min-width: 631px) and (max-width: 960px) {
	.status__content, .status__relative-time, .display-name__html, .display-name__account, .column-link {
		font-size: small;
	}
	.status__avatar,
	.account__avatar {
		width: 32px !important;
		height: 32px !important;
		background-size: 32px 32px !important;
	}
	.account__avatar-overlay {
		display: none;
	}
	.notification__message {
		position: relative;
		left: -16px;
	}
	.muted > .status__content {
		position: relative;
		left: -16px;
		width: calc(100% + 16px)
	}
	/* レイアウト調整 */
	.status {
		padding-left: 48px;
	}
	.notification__message {
		margin-left: 48px;
	}
}
