style.css:
@-moz-document url-prefix("https://mp.weixin.qq.com/s"), 
url-prefix("https://mp.weixin.qq.com/mp/appmsg/show"),
url-prefix("http://mp.weixin.qq.com/s"), 
url-prefix("http://mp.weixin.qq.com/mp/appmsg/show")
{
    .rich_media_empty_extra,
    .rich_media_inner,
    .rich_media_area_primary,
    .qr_code_pc,
    #js_content,
    #img-content,
    body,
    h1,h2,h3,
    pre,code,blockquote,
    section,
    p,
    span {
        font-family: Georgia !important;
        font-size: 13px !important;
        background: #333 !important;
        color: #ccc !important;
        border-color: #666 !important;
    }

    img {
        border-radius: 2px !important;
    }
    #page-content,.page-bizinfo{
        border-style: none !important;
    }
}
