style.css:
@-moz-document domain("youtube.com") {
	* {
		outline: none !important;
	}

	html, body,
	ytd-page-manager > *.ytd-page-manager,
	ytd-watch-flexy[flexy_][flexy-v2_][is-two-columns_] #columns.ytd-watch-flexy,
	#home-page-skeleton,
	#home-container-skeleton,
	ytd-watch-flexy[flexy_][is-two-columns_] #columns.ytd-watch-flexy{
		letter-spacing: 0.2px;
		background-color: rgba(38, 43, 55, 1) !important;
	}


	/* CARDS */

	ytd-post-renderer[uses-compact-lockup_] {
		background-color: #313745;
		border-radius: 4px;
		box-shadow: 0 2px 2px 0 rgba(0,0,0,0.15), 0 1px 4px 0 rgba(0,0,0,0.18), 0 3px 1px -2px rgba(0,0,0,0.2) !important;
		border: none;
	}

	ytd-comment-action-buttons-renderer {
		color: #ccc;
	}


	/* ACCOUNT */

	ytd-active-account-header-renderer,
	ytd-account-settings {
		background-color: #151924 !important;
	}

	ytd-simple-menu-header-renderer,
	.header.ytd-account-settings {
		background: transparent !important;
	}

	paper-item.ytd-account-settings {
		min-height: 40px !important;
	}

	paper-item.ytd-account-settings:hover {
		background-color: #353a45 !important;
	}

	.ytd-account-settings {
		color: #ceced1 !important;
		font-weight: 400 !important;
	}

	[is=yt-endpoint]:hover {
		background-color: #353a45 !important;
	}


	/* PLAYLIST */

	.header.ytd-playlist-panel-renderer,
	.playlist-items.ytd-playlist-panel-renderer {
		background-color: rgba(0,0,0,0.3) !important;
	}


	/* VIDEO TITLE */

	.title.ytd-video-primary-info-renderer {
		color: #dce1eb;
		font-size: 22px !important;
	}

	#video-title.yt-simple-endpoint.ytd-grid-video-renderer {
		color: #dce1eb !important;
	}

	.title.ytd-guide-entry-renderer {
		color: #dce1eb !important;
	}


	/* VIDEO DESC */

	.content.ytd-video-secondary-info-renderer {
	   color: #cbd1dd !important;
	}

	.count-text.ytd-comments-header-renderer {
		color: #cbd1dd !important;
	}

	#owner-name.ytd-video-owner-renderer {
		font-size: 1.6rem !important;
		margin-bottom: 4px !important;
	}


	/* VIDEO MENU BTN TEXT */

	ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer {
		font-size: 15px !important;
	}


	/* COMMENT TEXT, RELATED VIDS TITLE TEXT */

	#content-text.ytd-comment-renderer,
	#video-title {
		color: #cbd1dd !important;
		font-size: 14px !important;
	}


	/* COMMENT USERNAME, MORE/LESS BTN */

	#author-text.yt-simple-endpoint.ytd-comment-renderer,
	.more-button.ytd-comment-renderer, .less-button.ytd-comment-renderer {
		color: rgba(140, 202, 255, 0.9) !important;
		font-size: 13px !important;
	}


	/* COMMENT USERNAME CREATOR */

	ytd-author-comment-badge-renderer.creator {
	   background-color: rgb(72, 122, 235) !important;
	}

	ytd-author-comment-badge-renderer.creator yt-formatted-string {
		color: white !important;
	}


	/* LIVE BADGE */

	.badge-style-type-live-now.ytd-badge-supported-renderer {
		background: #000 !important;
	}


	/* NAVBAR, CHANNEL HEADER, NOTIFICATIONS BOX */

	#masthead,
	#channel-header.ytd-c4-tabbed-header-renderer,
	#tabs-inner-container.ytd-c4-tabbed-header-renderer,
	ytd-multi-page-menu-renderer {
		background-color: rgba(21, 25, 36, 1) !important;
		box-shadow: 0 6px 50px rgba(0, 0, 0, 0.3) !important;
	}

	#guide-icon.ytd-masthead,
	#logo-icon-container.ytd-topbar-logo-renderer #youtube-paths.ytd-topbar-logo-renderer path.ytd-topbar-logo-renderer {
		fill: #fff !important;
	}


	/* CHANNEL PAGES */

	#channel-title.ytd-c4-tabbed-header-renderer {
		color: #ccc !important;
	}

	paper-tab.iron-selected.ytd-c4-tabbed-header-renderer {
		color: #fff !important;
	}

	#selectionBar.paper-tabs {
		border-bottom: 2px solid #999 !important;
	}


	/* POPOVERS */

	paper-menu,
	paper-listbox,
	paper-dialog,
	ytd-modal-with-title-and-button-renderer,
	.ytd-popup-container {
		background-color: #313745 !important;
		border-radius: 2px;
		box-shadow: 0 2px 2px 0 rgba(0,0,0,0.15), 0 1px 4px 0 rgba(0,0,0,0.18), 0 3px 1px -2px rgba(0,0,0,0.2) !important;
	}

	paper-listbox,
	ytd-menu-popup-renderer.ytd-popup-container {
		box-shadow: none !important;
	}

	.dropdown-content.paper-menu-button {
		background-color: transparent !important;
	}

	a.ytd-menu-navigation-item-renderer:hover,
	a.yt-dropdown-menu:hover,
	ytd-single-option-survey-option-renderer[vertical]:hover {
		background-color: rgba(255,255,255,0.1) !important;
	}

	.ytd-mealbar-promo-renderer-message-title,
	.ytd-modal-with-title-and-button-renderer{
		color: rgba(200, 210, 220, 1) !important;
	}

	.ytd-mealbar-promo-renderer-message-text {
		color: rgba(180, 190, 200, 0.8) !important;
	}

	.button-container,
	.buttons.ytd-modal-with-title-and-button-renderer {
		border-top: 1px solid #4f5666 !important;
	}


	/* VIDEO PAGE */

	#vote-count-left.ytd-comment-action-buttons-renderer {
		color: rgba(180, 190, 200, 0.8) !important;
	}


	/* SORT BY */

	#icon-label.yt-dropdown-menu,
	.item.yt-dropdown-menu {
		color: rgba(180, 190, 200, 0.8) !important;
	}

	#label-icon.yt-dropdown-menu {
		fill: rgba(180, 190, 200, 0.8) !important;
	}


	/* THUMBNAILS */

	ytd-thumbnail.ytd-compact-video-renderer,
	ytd-thumbnail.ytd-video-renderer {
		box-shadow: 0 4px 20px -4px rgba(0, 0, 0, 0.4) !important;
	}

	ytd-thumbnail.ytd-compact-video-renderer img,
	ytd-thumbnail.ytd-video-renderer img {
		border-radius: 2px !important;
	}


	/* SEARCH */

	#search-form.ytd-searchbox,
	ytd-searchbox[mode=legacy] #search-form.ytd-searchbox,
	ytd-searchbox[mode="legacy"] #container.ytd-searchbox,
	ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox,
	ytd-searchbox[mode="legacy-centered"] #search-form.ytd-searchbox {
		position: relative;
		max-width: 100% !important;
	}

	ytd-searchbox[mode=legacy] #container.ytd-searchbox,
	ytd-searchbox[mode="legacy"] #container.ytd-searchbox,
	ytd-searchbox[mode="legacy-centered"] #container.ytd-searchbox,
	#container.ytd-searchbox {
		padding: 4px 8px;
		background-color: rgba(255, 255, 255, 0.1);
		border: 1px solid transparent !important;
		box-shadow: none !important;
		border-radius: 18px !important;
		color: rgba(255, 255, 255, 0.75) !important;
	}

	#search-icon-legacy.ytd-searchbox {
		position: absolute;
		width: 36px;
		height: 36px;
		right: 8px;
		background-color: transparent !important;
		border: none;
	}

	ytd-searchbox[mode=legacy] #search-icon-legacy.ytd-searchbox:hover > yt-icon {
		color: rgba(255, 255, 255, 0.8) !important;
	}

	ytd-searchbox[mode=legacy] #search-icon-legacy.ytd-searchbox:hover {
		background-color: transparent;
	}

	.sbdd_b,
	.sbsb_a,
	.sbfl_b {
		background: #313745;
		border: none;
	}

	.sbfl_b {
		color: #999;
	}

	.sbsb_c {
		color: #ccc;
	}

	.sbsb_c:last-child {
		margin: 0 12px;
		padding: 4px 18px 4px 0;
	}

	.sbsb_c:hover {
		background: rgba(255,255,255,0.1);
	}

	.sbpqs_a {
		color: #9aadd9;
	}


	/* SIDEBAR */

	ytd-guide-renderer.ytd-app,
	ytd-mini-guide-renderer[background-color-update_],
	ytd-mini-guide-entry-renderer[background-color-update],
	#guide-inner-content.ytd-app,
	html[dark] #guide-skeleton,
	app-drawer.ytd-app:not([persistent]) #header.ytd-app {
		 background-color: rgb(34, 37, 48);
	}

	ytd-mini-guide-entry-renderer[background-color-update]:hover {
		background-color: rgb(54, 57, 68);
	}

	#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover {
		background-color: rgb(53, 58, 69) !important;

	}

	.title.ytd-mini-guide-entry-renderer,
	#guide-section-title.ytd-guide-section-renderer,
	#footer.ytd-guide-renderer > #copyright {
		color: rgba(180, 190, 200, 0.8);
	}

	.title.ytd-mini-guide-entry-renderer {
		font-weight: 500;
	}

	#guide-icon.ytd-app {
		fill: #fff !important;
	}


	/* MINOR TEXT */

	yt-formatted-string,
	#published-time-text.ytd-comment-renderer [is=yt-endpoint], 
	ytd-button-renderer #button.ytd-button-renderer, 
	yt-icon,
	#metadata-line.ytd-grid-video-renderer,
	.more-button.ytd-comment-replies-renderer, .less-button.ytd-comment-replies-renderer,
	.more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer,
	.date.ytd-video-secondary-info-renderer,
	#metadata.ytd-video-meta-block,
	.view-count,
	#title.ytd-metadata-row-renderer,
	#byline.ytd-video-meta-block,
	#metadata-line.ytd-video-meta-block,
	#label.ytd-pinned-comment-badge-renderer,
	paper-tab:not(.iron-selected) > .tab-content.paper-tab,
	#footer.ytd-guide-renderer  #guide-links-primary a, #footer.ytd-guide-renderer  #guide-links-secondary a,
	#autoplay.ytd-compact-autoplay-renderer,
	#title-annotation.ytd-shelf-renderer,
	paper-button.yt-next-continuation,
	#description-text.ytd-video-renderer,
	ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer,
	#subtitle.ytd-shelf-renderer,
	#published-time-text.ytd-comment-renderer,
	#upnext.ytd-compact-autoplay-renderer,
	.input-content.paper-input-container textarea,
	.input-content.paper-input-container iron-autogrow-textarea,
	.input-content.paper-input-container .paper-input-input,
	.input-content.paper-input-container label,
	input,
	yt-live-chat-renderer,
	.yt-live-chat-text-message-renderer,
	.yt-live-chat-header-renderer,
	#author-name.yt-live-chat-message-input-renderer,
	#count.yt-live-chat-message-input-renderer,
	#label.yt-live-chat-text-input-field-renderer,
	#search-panel input,
	.description.ytd-toggle-item-renderer,
	ytd-toggle-theme-compact-link-renderer,
	ytd-searchbox[mode=legacy] #container.ytd-searchbox input.ytd-searchbox,
	ytd-searchbox[mode=legacy-centered] #container.ytd-searchbox input.ytd-searchbox,
	#title.ytd-vertical-channel-section-renderer,
	.title.ytd-mini-channel-renderer,
	#label-text.yt-dropdown-menu,
	#title.ytd-grid-channel-renderer,
	#thumbnail-attribution.ytd-grid-channel-renderer {
		color: rgba(180, 190, 200, 0.8) !important;
	}

	iron-icon {
		fill: rgba(180, 190, 200, 0.8) !important;
	}

	#title.ytd-shelf-renderer,
	a.yt-simple-endpoint.yt-formatted-string,
	button[is='paper-icon-button-light'].ytd-masthead,
	ytd-grid-video-renderer.grid #byline.ytd-grid-video-renderer {
		color: #dce1eb !important;
	}


	/* VARIOUS BORDERS */

	#contents.ytd-section-list-renderer > *.ytd-section-list-renderer:not(:last-child),
	#sections.ytd-guide-renderer > *.ytd-guide-renderer:not(:last-child),
	app-drawer.ytd-app:not([persistent]) #header.ytd-app,
	ytd-compact-autoplay-renderer,
	ytd-video-primary-info-renderer,
	ytd-video-secondary-info-renderer,
	ytd-account-section-list-renderer,
	#placeholder-area.ytd-comment-simplebox-renderer,
	#sections.ytd-multi-page-menu-renderer > *.ytd-multi-page-menu-renderer:not(:last-child),
	#description-container.ytd-channel-about-metadata-renderer,
	#links-container.ytd-channel-about-metadata-renderer,
	#right-column.ytd-channel-about-metadata-renderer > yt-formatted-string.ytd-channel-about-metadata-renderer {
		border-bottom: 1px solid #373c47 !important;
	}

	.focused-line {
		background: #fff !important;
	}

	.arrow.yt-horizontal-list-renderer {
		background-color: #313847 !important;
	}


	/* LIKE BUTTON */

	ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer .style-default-active {
		color: white !important;
	}

	#top-level-buttons ytd-toggle-button-renderer.style-default-active:first-child yt-icon,
	#like-button .style-default-active > yt-icon {
		color: #5fd9b1 !important;
	}

	#top-level-buttons ytd-toggle-button-renderer.style-default-active:nth-child(2) yt-icon,
	#dislike-button .style-default-active > yt-icon {
		color: hsl(3, 100%, 66%) !important;
	}


	/* COMMENT BUTTON */

	ytd-button-renderer #button.ytd-button-renderer.style-primary {
		color: white !important;   
	}

	paper-button {
		background-color: transparent;
	}


	/* VIEWS */

	.view-count {
		font-size: 18px;
	}


	/* RED ICON */

	ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer {
		color: rgba(255, 78, 78, 0.83) !important;
	}


	/* RED 'NEW' ICON */

	.badge-style-type-simple.ytd-badge-supported-renderer {
		background-color: rgba(96, 100, 118, 0.83) !important;
		color: rgba(255, 255, 255, 0.78) !important;
	}


	/* LINKS */

	[is=yt-endpoint],
	.yt-simple-endpoint,
	paper-tab:not(.iron-selected):hover > .tab-content {
	   color: rgba(140, 202, 255, 0.9) !important;
	}

	[is=yt-endpoint]:hover,
	.yt-simple-endpoint:hover {
		color: rgba(180, 230, 255, 1) !important;
	}


	/* SUBSCRIBE BUTTON */

	paper-button.ytd-subscribe-button-renderer,
	ytd-button-renderer.style-destructive[is-paper-button],
	ytd-button-renderer.style-compact-gray[is-paper-button] {
		background: linear-gradient(90deg,hsl(341, 80%, 49%), hsl(3, 81.8%, 49.6%)) !important;
		transition: all 0.2s ease !important;
	}

	paper-button[subscribed].ytd-subscribe-button-renderer {
		background: hsl(0, 0%, 93.3%) !important;
	}


	/* COMMENT VOTE COUNT */

	#vote-count.ytd-comment-action-buttons-renderer {
		color: #5fd9b1 !important;   
	}


	/* LIKE BAR */

	#sentiment.ytd-video-primary-info-renderer {
		width: 255px !important;
		height: 3px !important;
		margin-top: 0 !important;
	}

	#like-bar.ytd-sentiment-bar-renderer {
		height: 3px !important;   
	}


	/* LIVESTREAM */

	ytd-menu-service-item-renderer:hover,
	ytd-toggle-theme-compact-link-renderer:hover {
		background: rgba(255, 255, 255, 0.1) !important;
	}

	yt-live-chat-header-renderer {
		background-color: transparent !important;
	}

	yt-live-chat-app,
	.yt-live-chat-renderer {
		background-color: #1c2029 !important;
	}

	.ytd-live-chat-frame ytd-toggle-button-renderer #button.ytd-toggle-button-renderer {
		background-color: #212630 !important;
	}

	#search-panel.yt-emoji-picker-renderer {
		background-color: #2d303a !important;
	}

	#menu.yt-live-chat-text-message-renderer {
		background: transparent !important;
	}

	yt-formatted-string.yt-emoji-picker-category-renderer {
		background: #272c38 !important;
		border-radius: 2px !important;
	}

	.yt-live-chat-renderer-0,
	.yt-live-chat-gaming-promo-renderer-0 #container.yt-live-chat-gaming-promo-renderer,
	.yt-live-chat-header-renderer-0,
	.yt-live-chat-message-input-renderer-0,
	.yt-emoji-picker-category-renderer-0 yt-formatted-string.yt-emoji-picker-category-renderer {
		background-color: rgba(0,0,0,0.3) !important;
	}

	#show-hide-button.ytd-live-chat-frame {
		background-color: rgba(0,0,0,0.5) !important;
	}

	.paper-menu-0 {
		background-color: #313745 !important;
	}
}
