style.css:
@-moz-document domain("groupme.com") {
@font-face {
	font-family: 'Roboto';
	font-style: normal;
	font-weight: 400;
	src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v18/CWB0XYA8bzo0kSThX0UTuA.woff2) format('woff2');
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}

body {
    background-color: #36393e;
    font-family: 'Roboto',Helvetica Neue,Helvetica,Arial,sans-serif;
    color: #fff;
    text-rendering: optimizeLegibility;
    line-height: 1;
}

h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: #fff;
}

[class^="gmicon-"], [class*=" gmicon-"] {
    color: #7289DA;
}

.fancy-toggle input[type=checkbox] + button .on {
    color: #7289DA;
}

.categories .btn-link.active:before {
    background-color: #7289DA;
}

#chats .chat-header .menu-toggle .toggle-arrow .caret {
    color: #7289DA;
}

.pillar {
    background-color: #202225;
    border: none;
}

.pillar .tab {
    color: #7289DA;
    border: none;
    width: 48px;
}

.pillar .tab.active {
    background-color: #2f3136;
}

.pillar .tab.logo:hover, .pillar .tab.logo:focus, .pillar .tab.logo.active {
    background-color: #2f3136;
}

#tray {
    background-color: #2f3136;
    border: none;
    box-shadow: 0 1px 0 rgba(0,0,0,.2),0 2px 0 rgba(0,0,0,.06);
}

#tray .tray-header h3 {
    color: #fff;
}

#tray .tray-header .row {
    background: transparent;
    border: none;
}

#tray .list-search {
    background-color: transparent;
    border: none;
}

#tray .list-search input {
    background: rgba(0,0,0,.2);
    border: 1px solid rgba(0,0,0,.1);
    margin: 8px 16px;
    height: 26px;
    color: #fff;
    outline: none;
}

#tray .tray-list {
    background-color: transparent;
}

#tray .tray-list::-webkit-scrollbar {
    width: 8px;
}

#tray .tray-list::-webkit-scrollbar-thumb {
	background-color: #1e2124;
    border-radius: 4px;
}

#tray .tray-list .list-item .list-heading {
    font-family: 'Roboto';
    color: #b9bbbe;
    font-size: 16px;
}

#tray .tray-list .list-item:hover .list-heading {
    color: #fff;
}

#tray .tray-list .list-item.active .list-heading {
    color: #fff;
}

#tray .tray-list .list-item .list-heading .list-title::before {
    content: '#';
    color: #b9bbbe;
}

#tray .tray-list .list-item .list-heading .list-title .chat-name {
    max-width: 200px;
}

#tray .tray-list .timestamp {
    display: none;
}

#tray .tray-list .list-content {
    height: 0;
}
  
#tray .tray-list .list-content .message-preview {
    display: none;
}
  
#tray .tray-list .list-content .badge-count {
    top: -5px;
    left: -30px;
    background-color: #7289DA;
}

.groupme-list .list-item {
    background-color: transparent !important;
    border: none;
    padding: 5px 16px;
    height: 77px;
}

.groupme-list .list-item:hover {
	background-color: transparent;
}

.groupme-list .list-item .wrapper {
    padding: 8px;
    display: table;
}

.groupme-list .list-item .wrapper .list-body {
    display: table-cell;
    vertical-align: middle;
    width: 100%;
}

.groupme-list .list-item:hover .wrapper {
    background-color: #36393f;
    border-radius: 3px;
}

.list-item .list-gutter .avatar {
    -webkit-transition:all cubic-bezier(.17,.67,.49,1) 0.3s;
    -moz-transition:all cubic-bezier(.17,.67,.49,1.8) 0.3s;
    -ms-transition:all cubic-bezier(.17,.67,.49,1) 0.3s;
    -o-transition:all cubic-bezier(.17,.67,.49,1) 0.3s;
    transition:all cubic-bezier(.17,.67,.49,1.8) 0.3s;
}

.list-item:hover .list-gutter .avatar {
    border-radius: 15px;
}

.list-item.active .list-gutter .avatar {
    border-radius: 15px;
}

.list-gutter .avatar {
    border-radius: 50%;
}

.groupme-list .alpha-header {
    color: #fff;
    background-color: #202225;
}

#chats .chat-header {
    background-color: #36393e;
  	z-index: 9000;
    border: none;
    box-shadow: 0 1px 0 rgba(0,0,0,.2),0 2px 0 rgba(0,0,0,.06);
    padding-left: 20px;
}

#chats .chat-header .avatar-wrap {
    display: none;
}

.chat-menu {
    background-color: #202225;
    border: none;
}

.gm-group-topic .topic i {
    color: #7289DA;
}

.chat-menu .section:hover > i {
    color: #7289DA;
}

.chat-menu .section > span, .chat-menu .section:hover > span {
    color: #fff;
}

#chats .chat {
    border: none;   
}

#chats .chat:only-child {
    max-width: none;
    width: 100%;
}

#chats .chat:not(:last-child) {
    border-right: 1px solid #2f3136;
}

.chat-messages::-webkit-scrollbar {
    width: 8px;
}

.chat-messages::-webkit-scrollbar-thumb {
	background-color: #1e2124;
    border-radius: 4px;
}

.chat-messages {
    background-color: transparent;
}

.chat-messages .message.me {
    background-color: transparent;
}

.chat-messages .message.highlight, .chat-messages .message.me.highlight {
    background-color: transparent;
}

.chat-messages .message.system .message-body .message-text {
    color: #fff;
}

.chat-messages .message.system {
    background-color: transparent;
}

.chat-messages .timestamp-divider {
    color: #fff;
    background-color: #2f3136;
    margin: 0 6px 0 14px;
}

.chat-messages .message .avatar-crop {
    position: absolute;
    left: 9px;
    height: 40px;
    width: 40px;
}

.chat-messages .message .message-header {
    position: relative;
}

.chat-messages .message .message-header .nickname {
    font-size: 16px;
    color: #7289DA;
    margin-bottom: 5px;
}

.chat-messages .message .message-body .content .message-text {
    font-size: 15px;
}

.pop-over {
    background-color: #202225;
}

.pop-over::after {
    border-top: 6px solid #202225;
}

.liker {
    width: 25px;
    height: 25px;
}

.liker:not(:last-child) {
    margin-right: 2px;
}

.chat-input {
    margin: 0px 20px;
    z-index: 1000;
    background-color: #36393e;
}

.message-composer {
    background-color: transparent;
    margin: 35px 0px 20px;
    border: none;
}

.composer-layout {
    background-color: hsla(218,5%,47%,.3);
    border-radius: 5px;
}

.message-composer .composer-layout .composer-wrapper div[contenteditable="true"]:focus {
    box-shadow: none;
}

.message-composer .composer-layout .controls {
    bottom: auto;
    top: 50%;
    transform: translateY(-50%);
}

.chat-messages .pages {
    padding-bottom: 0px;
}

.modal-content {
    background-color: #36393e;
}

.modal-body::-webkit-scrollbar {
    width: 8px;
}

.modal-body::-webkit-scrollbar-thumb {
	background-color: #1e2124;
    border-radius: 4px;
}

.categories .btn-link, .categories .btn-link.active, .categories .btn-link:hover {
    color: #fff;
}

.poll-nav {
    background-color: #36393e;
    border: none;
}

.dropdown-switcher {
    border: none;
}

.close {
    color: #fff;
}

.popular .all-likes-filter {
    border: none;
}

.popular .chat-messages .popular-message {
    border: none;
}

.popular .popular-nav {
    border: none;
}

.popular .categories .btn-link, .popular .categories .btn-link:hover, .popular .categories .btn-link.active {
    color: #fff;
}

.popular .categories .btn-link.active:before {
    background-color: #7289DA;
}

.popular .chat-messages .popular-message .popular-timestamp.me {
    background-color: transparent;
}

.control-label {
    color: #000;
}

.modal-content .avatar-wrap {
    background-color: transparent;
}

.group-settings .form-control {
    background-color: #202225;
    border: none;
    outline: none;
}

.modal.new-group .search, .modal.change_owner .search, .modal.new-dm .search {
    border: none;
}

.contacts-pane::-webkit-scrollbar {
    width: 8px;
}

.contacts-pane::-webkit-scrollbar-thumb {
	background-color: #1e2124;
    border-radius: 4px;
}

.contacts-list .list-item .list-heading {
    font-family: 'Roboto';
    color: #b9bbbe;
    font-size: 16px;
}

.contacts-list .list-item:hover .list-heading {
    color: #fff;
}

.contacts-list .list-item.active .list-heading {
    color: #fff;
}

.contacts-list .list-item .list-heading .list-title::before {
    content: '#';
    color: #b9bbbe;
}

.contacts-list .list-item .list-heading .list-title .chat-name {
    max-width: 200px;
}

.contacts-list .timestamp {
    display: none;
}

.contacts-list .list-content {
    display: none;
}

.contacts-list .list-item {
    background-color: transparent !important;
    border: none;
    padding: 5px 16px;
    height: 77px;
}

.contacts-list .list-item:hover {
	background-color: transparent;
}

.contacts-list .list-item .wrapper {
    padding: 8px;
    display: table;
}

.contacts-list .list-item .wrapper .list-body {
    display: table-cell;
    vertical-align: middle;
    width: 100%;
}

.contacts-list .list-item:hover .wrapper {
    background-color: #36393f;
    border-radius: 3px;
}

#typing {
    background-color: transparent;
    color: #b9bbbe;
    top: -25px;
}

#typing img {
    content: url(https://i.imgur.com/1OUel5F.gif);
    width: 40px;
    margin-left: 5px;
}
  
.inline-tweet {
    color: #000;
}
  
#notifications {
    top: 55px;
}
  
#notifications .notification {
    border: none;
    box-shadow: none;
    background-color: #505358;
}
  
#notifications .notification .message-content {
    color: #fff;
}
  
#notifications .notification .avatar {
    border-radius: 10px;
}
  
#notifications .notification .close, .close:hover, .close:focus {
    color: #7289DA;
    opacity: 1;
}
}
