style.css:
@-moz-document domain("manhua.dmzj.com"), domain("manhua.dmzj1.com")
{

iframe,
body>div:not([id]):not([class]),
a[href="http://www.dmzj.com/app/pc.html"],
#note,
#floatCode,
#app_manhua,
.header-box,
.footer,
.box_text,
.hotrmbox,
.recom_one,
.sharebox,
.ad_six,
.ad_bottom_code,
.funcdiv,
.login_tip,
.user_show,
.wrap_last_head .head_title,
.wrap_last_mid {
	display: none !important
}

body {
	min-height: calc(100vh - 16mm);
	padding: 5mm 0;
	background: radial-gradient(circle at 60vw 25mm,
			#ddd 200px,
			#999 600px,
			#555 100vh,
			#555 1000vh);
}

img {
	object-fit: contain
}

html, body, div, h1, h2, h3, h4, h5, h6, p, form, ul, li, dl, dt, dd, ol, table,
.tcaricature_block2 ul li a {
	font: 400 11pt sans-serif;
}

#hd {
	width: auto!important;
	padding: 3mm 15%!important;
	background: rgba(255,255,255,.3);
	border: none!important;
}

.logobox {
	min-height: 120px;
}

.qqnew {
	text-shadow: 0 0 2mm #fff;
}

.navbg1 {
	margin-top: 0;
}

.warp {
	background: rgba(255, 255, 255, .7);
	padding: 0 8mm;
}

.wrap {
	position: relative;
	width: auto;
	height: auto;
	background: rgba(255, 255, 255, .6);
	margin: 0 auto;
	padding: 0 0 2mm 8mm;
	overflow: hidden;
	box-shadow: 0 0 2mm #888
}

.footer {
	background-color: transparent
}

.leftmiddle {
	display: inline-block;
	width: calc(100% - 330px);
	float: none;
	background: #eee;
	padding-bottom: 5mm;
	margin: 0 auto;
}

.middleright {
	display: inline-block;
	width: calc(100% - 250px);
}

.right {
	display: inline-block;
	background: rgba(220, 220, 220, .7);
	padding: 5mm;
	border-radius: 3mm 0 0 3mm;
	box-shadow: 0 0 3mm #555;
	transition: .5s;
}
.apinknum {
	top: 0;
}
.search_content {
	background-color: white
}

.display_graybg {
	background-color: transparent !important
}

.search_content,
.anim_search_list {
	max-height: 5em;
	overflow: hidden;
	border-radius: 2mm;
	transition: .8s .5s
}

.search_content:hover,
.anim_search_list:hover {
	max-height: 25em;
	transition: .3s .3s
}

.search_list_m_right {
	width: calc(100% - 110px);
}

.tcaricature_block,
.tcaricature_block2 {
	font-size: 0
}

.tcaricature_block ul,
.tcaricature_block2 ul {
	display: inline-block;
	float: none;
	height: auto;
	padding: 2mm;
	margin: 3px 0 !important;
	overflow: hidden;
	border-radius: 1.5mm;
	transition: .2s
}

.tcaricature_block ul:hover,
.tcaricature_block2 ul:hover {
	background-color: rgba(225, 245, 255, .74);
	box-shadow: inset 0 0 0 1px #944, 0 1mm 3mm rgba(0, 0, 0, .4);
	transition: .1s
}

.cartoon_online_button,
.tcaricature_block ul li,
.adiv,
.adivhidden {
	width: auto
}

.adiv {
	white-space: nowrap;
	text-overflow: ellipsis
}

.tcaricature_block2 ul li {
	display: block !important;
	width: 100%;
	line-height: 1.7;
	margin: 0 auto;
	white-space: nowrap;
	text-overflow: ellipsis;
	overflow: hidden
}

.tcaricature_block ul li img,
.tcaricature_block2 ul li img {
	width: 100%;
	height: auto;
	min-height: 180px;
	max-height: 320px;
	padding: 0;
	object-fit: cover;
	border: none;
	border-radius: 1mm
}

.tcaricature_block2 ul li a:hover img {
	border: none;
	transition: 0s
}

.finalbox {
	background: #eee
}

.wrap_last_head {
	margin-top: 2mm
}

#center_box img,
.comic_wraCon.autoHeight img {
	width: auto !important;
	min-width: 40vw;
	max-width: calc(100vw - 5mm);
	max-height: 96vh;
	background: rgba(0, 0, 0, .1);
	margin: 6.5mm 2mm 0;
	padding: 0 !important;
	border: none;
	border-radius: 2mm;
	box-shadow: 0 0 3mm gray;
	user-select: none;
	-webkit-user-select: none
}

#center_box img {
	height: calc(100vh - 13mm) !important
}

.img_land_prev,
.img_land_next {
	margin-top: 1%;
	width: 40% !important;
	height: 98% !important
}

.img_land_prev {
	cursor: url('data:image/svg+xml;base64,') 16 16, auto
}

.img_land_next {
	cursor: url('data:image/svg+xml;base64,') 16 16, auto
}

.mainNav {
	border-bottom: 1px #aaa solid;
	padding: .5em 0;
	background: rgba(0, 0, 0, .5);
	position: fixed;
	top: -30px;
	z-index: 100;
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	transition: top .8s .4s, background .8s .2s;
	box-shadow: 0 0 2mm #aaa
}

.mainNav_in_l a,
.mainNav_in_r ul li a {
	font: 400 12pt sans-serif;
	line-height: 1.5em !important;
	height: auto !important;
	padding: .2em .5em !important;
	margin: 0 !important;
	border-radius: 1em;
	transition: all .2s
}

.mainNav a:hover {
	background-color: rgba(255, 255, 255, .7);
	text-decoration: none;
	box-shadow: inset 0 0 0 1px rgba(0, 0, 0, .3);
	transition: all 0s
}

.mainNav.independNav {
	background: rgba(30, 30, 180, .2);
	top: -36px
}

.mainNav:hover {
	background-color: rgba(220, 220, 220, .8);
	top: 0;
	transition: top .2s .3s, background .2s
}

.mainNav.independNav:hover {
	background-color: rgba(60, 100, 180, .8)
}

.mainNav_inner {
	width: auto;
	min-width: 900px;
	max-width: 95vw
}

.btmBtnBox {
	position: fixed;
	bottom: -22px;
	padding: 0 !important;
	margin: 0 !important;
	width: 100% !important;
	line-height: 40px;
	background-color: rgba(0, 0, 0, .5);
	opacity: .6;
	transition: background-color .5s, bottom .5s;
	backdrop-filter: blur(2mm);
	-webkit-backdrop-filter: blur(2mm);
	z-index: 99
}

.btmBtnBox:hover {
	transition: background-color .2s, bottom .2s;
	bottom: 0;
	opacity: 1;
	background-color: rgba(0, 0, 0, .5)
}

.btmBtnBox #page_select {
	float: none !important;
	margin: 0.8em 0 0 65% !important
}

.book_wrap {
	position: fixed;
	z-index: 101;
	transition: all 0.5s
}

.book_open {
	padding: 0 !important;
	border: solid 2px #418fdf;
	background: white !important;
	border-radius: 3mm;
	box-shadow: 0 2mm 5mm gray
}

.book_open .book_con_li li:hover {
	background-color: rgba(200, 200, 200, .3)
}

.book_open .book_con_li li a.book_num {
	float: none;
	width: auto;
	display: block
}

.subcribe_new {
	position: absolute;
	top: -1px;
	right: 7.5em;
	z-index: 10
}

.comic_gd_con .comic_gd_li li a,
.comic_gd_con .comic_gd_li li a:hover {
	font: 400 12pt sans-serif;
	height: 1.3em;
	line-height: 1.5em;
	border-radius: 2em;
	margin: 0 .5mm;
	padding: .3em .8em;
	border: none
}

.comic_gd_con .comic_gd_li li a:hover {
	box-shadow: 0 0 1mm 1px red
}

.anim_search_list {
	background-color: rgba(0, 0, 0, .1);
	box-shadow: inset 0 0 2mm rgba(0, 0, 0, .4)
}

.tcaricature_block ul.intere_con {
	width: auto;
	max-width: 100%
}

.tcaricature_block ul.intere_con li {
	margin-right: 2.5mm
}

.tcaricature_block ul.intere_con li p {
	display: inline-block
}

.display_middle {
	white-space: nowrap
}

.display_middle .hotrmtexth1 {
	width: calc(100% - 5em);
	text-align: left;
	overflow: hidden;
	text-overflow: ellipsis
}

@media screen and (max-width: 980px) {

	.tcaricature_block ul,
	.tcaricature_block2 ul {
		width: calc(100%/4 - 4mm);
	}

	.tcaricature_block ul li img,
	.tcaricature_block2 ul li img {
		height: calc((100vw - 10mm - 250px) * .27);
	}
}

@media screen and (min-width: 980px) {

	.tcaricature_block ul,
	.tcaricature_block2 ul {
		width: calc(100%/5 - 4mm);
	}
}

@media screen and (min-width: 1300px) {

	.tcaricature_block ul,
	.tcaricature_block2 ul {
		width: calc(100%/6 - 4mm);
	}

	.tcaricature_block ul li img,
	.tcaricature_block2 ul li img {
		height: calc((100vw - 10mm - 250px) * .22);
	}
}

@media screen and (min-width: 1600px) {

	.tcaricature_block ul,
	.tcaricature_block2 ul {
		width: calc(100%/7 - 4mm);
	}

	.tcaricature_block ul li img,
	.tcaricature_block2 ul li img {
		height: calc((100vw - 10mm - 250px) * .19);
	}
}

@media screen and (min-width: 1800px) {

	.tcaricature_block ul,
	.tcaricature_block2 ul {
		width: calc(100%/8 - 4mm);
	}

	.tcaricature_block ul li img,
	.tcaricature_block2 ul li img {
		height: calc((100vw - 10mm - 250px) * .19);
	}
}
}
