style.css:
/* i really want this to be global */

/* Main */

#archnavbar {
	background-color: #2f343f !important;
}

body {
	background-color: #2f343f !important
}

div.box {
	background-color: #444b5d !important;
	border: none !important;
	color: #cfcfcf !important
}

#pkgsearch {
	background-color: #444b5d !important;
	border: none !important
}

input#pkgsearch-field {
	background-color: #444b5d !important;
	color: #cfcfcf !important;
}

div#news {
	color: #cfcfcf !important
}

code {
	color: #cfcfcf !important;
	background-color: #444b5d !important;
}

pre {
	background-color: #444b5d !important;
	border: none !important
}

a:visited {
	color: #737f9d !important
}

a:link {
	color: #cbd2db !important
}

div#footer {
	color: #c2c2c2 !important
}

span.community, span.more, span.next, span.prev {
	color: #cfcfcf !important;
	background-color: #444b5d !important;
}

span.testing.community-testing {
	color: #a3a3a3 !important;
	background-color: #444b5d !important;	
}

code {
	background: #444b5d !important
}

/* Packages List */

.results, th, td {
	background-color: #444b5d !important;
	color: #cfcfcf
}

select#id_arch, input#id_q, select#id_repo, select#id_maintainer, select#id_flagged, input {
	background-color: #444b5d !important;
	color: #cfcfcf !important;
	border-width: 1px !important;
	border-style: solid !important
}

div#actionlist, div#elsewhere.widget {
	background-color: #2f343f !important;
	color: #cfcfcf !important;
	border: none !important
}

h4 {
	color: #cfcfcf !important
}

div.commit-subject, div.commit-msg {
	color: #cfcfcf !important
}

div.path {
	background-color: #444b5d !important;
}


/* Bugs */

div#search, div#pm-menu, div#menu {
	background-color: #444b5d !important;
	color: #cfcfcf !important;	
	border-bottom-color: #444b5d !important;
	border-left-color: #444b5d !important;
	border-top-color: #444b5d !important;
	border-right-color: #444b5d !important;
}

div#intromessage {
	color: #c2c2c2 !important
}

div#tasklist, table, td {
	background-color: #444b5d !important;
	border-bottom-color: #444b5d !important;
	border-left-color: #444b5d !important;
	border-top-color: #444b5d !important;
	border-right-color: #444b5d !important;
}

div#taskdetails, div.attachments, .summary .severity1, .severity2, .severity3, .severity4, .severity5 {
	background-color: #444b5d !important;
	border: none !important
}

div#related.tab {
	background-color: #444b5d !important;
}

div.attachments {
	border: dotted !important
}

div#fineprint {
	color: #c2c2c2 !important
}

h2.summary.severity5, span#navigation {
	background-color: #2f343f !important;
	color: #c2c2c2 !important
}

div#comments, a.active {
	background-color: #444b5d !important;
	color: #c2c2c2 !important;
	border: none !important
}

/* Security */

h1, b {
	color: #c2c2c2 !important
}

/* AUR */

div.comments.package-comments {
	color: #c2c2c2 !important
}
