style.css:
@-moz-document domain(podio.com) {
@media (max-width: 992px) {
.space-switcher-wrapper { width: 20%; }
.page-width { min-width: unset; }
.global-container { min-width: unset; }
.grid-column-1-2 { width: 100%; }
.grid-column-3-4 { width: 100%; }
.grid-column-1-4 { width: 100%; }
.gridster [data-sizex="1"] { width: 49%; }
.gridster [data-col="2"] { left: 50%; }
.app-view { min-width: unset; }
.app-view__content { min-width: -moz-available; min-width: -webkit-fill-available; }
.toggle-items-link { width: 32px; height: 54px; }
.item-overlay { min-width: unset; padding: 0 10px; }
.item-overlay-wrapper { min-width: unset !important; }
.edit-template { display: none; }
.gridster-block { pointer-events: none; }
.gridster-widget div a { pointer-events: auto; }
}
}
