style.css:
@-moz-document domain('tarvalon.net') {
	
	/* Remove the background image */
	body {
    	background-image: none;
	}

	/* Move the sidebar to the bottom */
	div.mainContainer {
    	margin-right: 0;
	}
	div.mainContent {
    	margin-right: 0;
	}
	div.sidebar {
    	float: none;
    	width: 100%;
	}
	div.sidebar.b-rightWidgets {
    	margin-left: 0;
	}

	/* Remove centre-column background */
	div#content div.pageContent {
    	background: none;
    	border: none;
    	padding: 0;
	}

}
