style.css:
@-moz-document domain(discordapp.com) {
/*makes the server name header and voice buttons at the bottom transparent and black*/
.header-1tSljs,
.header-1tSljs:hover,
.popout-open .header-1tSljs{
    background-color: rgba(205, 143, 255, 0.50)!important;
}
.flexChild-1KGW5q
{
    background-color: rgba(0, 0, 0, 0)!important;
}
.container-iksrDt
{
    background-color: rgba(0, 0, 0, 0.50)!important;
}
/*background*/

.friends-icon{
	background-image: url(https://media.giphy.com/media/Uyq0zBrBgcnL2/giphy.gif) !important; /* IMPORTANT: For best result, use even dimensions */
    background-size: 45px!important;
    margin-top:-10px;
}
/* body transparency */

#app-mount,
.app .layers .layer,
.app .layers,
.typing,
.content,
.flex-spacer,
.container-RYiLUQ,
.messages-wrapper,
.popout-menu, /* Server Menu (Popout) */
.popout-menu-item, /* Server Menu (Popout item) */
#friends, /* Friends Section */
.container-2OU7Cz,
#rtc-connection,
.ui-standard-sidebar-view {
	background-color: transparent !important;
}

.titleBar-3_fDwJ { margin-top: 0px; padding-top: 4px; }

.messages-wrapper,
.friends-table,
.messages-wrapper,
#friends .friends-header,
.titleBar-3_fDwJ,
.content-region /* Settings - Right Side */ {
	background-color:  rgba(0,0,0,0.80) !important;
}

.channel-members,
.channels-3g2vYe /* Direct Messages Side Bar */ {
	background-color: rgba(0,0,0,0.80) !important;
}

.guilds-wrapper /* Guilds (Left SideBar) */,
.title-qAcLxz,
.headerBar-cxbhPD, /* Friends Section Header */
.sidebar-region, /* Settings - Left Side */
.chat form {
	background-color: rgba(0,0,0,0.60) !important;
}

.chat form { /* Chat Box */
	margin: 0px !important;
	padding-left: 20px;
	padding-right: 20px;
	/* height: 106px; */
	padding: 6.5px 20px;
	display: flex !important;
	flex-shrink: 0 !important;
	position: relative;
}

.channel-members h2 {
		width: 50%;
		color: #FFF !important;
		background-color: rgba(205, 143, 255, 0);
		text-align: center;
		border-radius: 10px;
		border-bottom:0px solid #000;
		border-top:0px solid #000;
		border-left: 50px;
		border-right: 50px;
		padding-top: 4px;
	  padding-bottom: 4px;
		margin: 40px auto 9px;
}

.channel-members .member:hover{
    background: rgb(128,128,128, 0.8) !important;
}
.channel-members .member:hover{
    background: rgba(0, 0, 0, 0.42) !important;
}

.popout-menu .popout-menu-item.leave {
	background: rgba(220,0,0,0.75) !important;
	color: #fff !important;
}

.popout-menu .popout-menu-item {
	background-color: rgba(0,0,0,0.75) !important;
	color: #b9bbbe !important;
}

.popout-menu .popout-menu-separator {
	border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgba(198, 156, 219, 0.8);
}

#friends .friends-table {
	margin-top: 0px;
	padding-top: 8px;
}

.theme-dark .friends-table .scroller-wrap ::-webkit-scrollbar-thumb,
.theme-dark .messages-wrapper .scroller-wrap ::-webkit-scrollbar-thumb {
	background-color: rgba(0, 0, 0, 0.50);
	border-color: transparent;
}

.theme-dark .friends-table .scroller-wrap ::-webkit-scrollbar-track-piece,
.theme-dark .messages-wrapper .scroller-wrap ::-webkit-scrollbar-track-piece {
	background-color: transparent;
	border-color: transparent;
}

.itemDefaultSelected-1UAWLe,
.itemBrandSelected-3LxxzT,
#bd-settings-sidebar .ui-tab-bar-item.selected,
.buttonBrandFilled-3Mv0Ra,
.themeDefault-3M0dJU.valueChecked-3Bzkbm,
.barFill-18ABna,
#bd-settingspane-container .ui-switch-item .ui-switch-wrapper .ui-switch.checked,
.bd-pfbtn,
.ui-standard-sidebar-view .bda-slist .bda-right button {
	background-color: rgba(198, 156, 219, 0.8);
	color: #FFF;
}

.buttonBrandFilled-3Mv0Ra:hover {
	background: rgb(128,128,128, 0.8) !important;
}

.itemBrand-mC9YR4 {
	color: rgba(198, 156, 219, 0.8);
}

.itemDanger-3m3dwx {
	color: #FFF;
	background-color: rgb(240, 71, 71);
}

.checkbox .checkbox-inner input[type=checkbox]:checked+span,
.themeClear-1NqU69.valueChecked-3Bzkbm,
.tab-bar .tab-bar-item.tab-bar-item-primary,
.tab-bar .tab-bar-item.selected,
.chat .new-messages-bar {
	background-color: rgba(198, 156, 219, 0.8) !important;
	border-color: rgba(198, 156, 219, 0.8);
  color: #FFF !important;
}

.mention { color: rgba(198, 156, 219, 0.8) }
.mention:hover { color: #FFF; background: rgb(128,128,128, 0.8) !important; }
.sidebar-region .sidebar { padding-bottom: 0px !important; }

.popout-menu .popout-menu-separator {
		border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgba(198, 156, 219, 0.8);
}

.popout-menu .popout-menu-item:hover {
	background: rgb(128,128,128, 0.8) !important;
	color: #fff !important;
}

.message-group .comment .markup pre code, /* Multi Line Code Block */
.message-group .comment .markup code.inline /* Inline Code Block */ {
	background-color: rgba(50,50,50,0.30) !important;
}

.message-group .comment .markup pre code /* Multi Line Code Block */ {
	border-style: solid !important;
	border-width: 1px !important;
	border-color: #ce422b !important;
  padding: 0 !important;
}

.item-2zi_5J[style*="background-color: rgb(114, 137, 218);"] { /* Radio Buttons */
	background-color: rgba(198, 156, 219, 0.8) !important;
	border-color: rgba(198, 156, 219, 0.8) !important;
}

.checkbox-1QwaS4[style*="border-color: rgb(114, 137, 218);"] { /* Check Box in The Radio Buttons */
	border-color: rgba(198, 156, 219, 0.8) !important;
}

.checkbox-1QwaS4[style*="border-color: rgb(114, 137, 218);"] polyline { /* Tick in the Check Box */
	stroke: rgba(198, 156, 219, 0.8) !important;
}

.itemDefault-3NDwnY:hover, /* Settings Normal Button */
.itemBrand-mC9YR4:hover, /* Discord Nitro Button*/
.ui-tab-bar-item:hover /* Better Discord Settings Button */{
	color: #FFF; background: rgb(128,128,128, 0.8) !important;
}

/*Transparency for voice*/

#voice-connection, #friends, .friends-header, .friends-table, .guilds-wrapper, .guild-header header, .channels-wrap, .private-channels .search-bar, .private-channels, .guild-channels, .account, .friend-table-add-header,
 .chat, .content, .layers, .title-wrap:not(.search-bar), .messages-wrapper, .messages .divider span, .messages .divider:before, .content, .message-group-blocked, .is-local-bot-message,
 .channel-members-loading, .channel-members-loading .heading, .channel-members-loading .member, .typing, .layer, .layers, .container-RYiLUQ, .theme-dark .ui-standard-sidebar-view, 
.theme-dark .ui-standard-sidebar-view .sidebar-region, .theme-dark .ui-standard-sidebar-view .content-region, .theme-dark .channel-members, .layer, .layers, .container-2OU7Cz,
 .theme-dark .title-qAcLxz, .theme-dark .chat form, .channels-3g2vYe, .theme-dark .friends-table, .theme-dark .messages-wrapper, .content .flex-spacer, .theme-dark .chat>.content, .theme-dark .chat, .container-2OU7Cz,
 .theme-dark .channel-members, .channel-members, .channels-3g2vYe, .guilds-wrapper, .search .search-bar, .theme-dark .chat form, .container-3lnMWU, .theme-dark .title-qAcLxz {
    background: rgba(0, 0, 0, 0.2) !important;
}
.theme-dark .layer, .theme-dark .layers, .typeWindows-15E0Ys {
    background: rgba(0, 0, 0, 0.1) !important;
}

/*messes with guilds*/

.guilds-wrapper {
  background: rgba(0, 0, 0, 0.09) !important;
}

.guilds-wrapper .guilds .guild .guild-inner a,
.guilds-wrapper .guilds .guild
{
	width: 42px;
	height: 42px;
}
.guilds-wrapper{
    width: 64px;
	background: rgba(0, 0, 0, 0.51);
}
.guilds-wrapper .guilds .friends-icon {
    width: 42px;
    height: 42px;
	background-position: center 9px;
	background-color: rgb(114, 137, 218);
}

.theme-dark .friends-table .messages .divider.divider-red>span,
.theme-dark .messages-wrapper .messages .divider.divider-red>span
{
	color: rgba(153,170,181,1);
}

/*makes the status buttons pop thingie*/

.channel-members .member .status {
	transition: all 150ms ease;
    will-change: border-color, width, height, padding;
}

.channel-members .member:hover .status, .channel-members .member.popout-open .status {
    width:100% !important;
    background: transparent;
    border-radius: inherit;
    height: 100%;
	right: -4px !important;
	bottom: -4px;
	padding: 2px;
	transition: all 150ms ease;
	border-width: 2px;
	margin: 0;
}



/*messages settings here*/
.chat .message-group 
{
    padding:10px 0 5px;
}
.chat .message-group ~ div:last-child 
{
    margin-bottom:15px
}
.chat .comment 
{
    display: flex; 
    flex-direction: column; 
    margin-top: -10px;
}
.chat .message-group .mentioned .message-text 
{
    background:transparent !important;
}
.chat .message-group .mentioned .message-text:after 
{
    border-left: 0; 
    background:transparent;
}
.chat .reactions 
{
    margin-top: -2px; 
    margin-bottom: 4px;
}
.chat .message:not(.first) 
{
    margin-top: -6px !important
}
.chat .markup .inline, .chat .markup .highlight 
{
    line-height: 24px;
}
.chat .markup 
{
    padding-top: 4px !important;
}
.chat .message-group .comment .edited 
{
    display: table; 
    margin-left: 0; 
    margin-top: 2px;
}
.chat .message-group a 
{
    color: #DDA5FD;
} 
.chat .edit-container-outer .avatar-large 
{
    opacity:0;
}
.chat .message-group .comment .markup:empty 
{
    padding:0; 
    min-width: 0px; 
    top:20px;
}
.chat .markup pre 
{
    max-width: 99% !important; 
    margin:2px; 
    overflow: hidden;
}
.chat .message-group .comment .markup pre code 
{
    word-break: break-all; 
    width: calc(100% - 8px);
}
.chat .message-text :-webkit-any(.btn-option, .btn-reaction)
{
    margin-top: 10px;
}
.chat .message-text div 
{
    order: 2;
}
.chat .message-text .markup 
{
    order:1;
}
.chat .message .message-text 
{
    display: flex; 
    width: auto;
}
.chat .message-group .comment .message.first 
{
    margin-top: 10px
}

.chat .message-group:not(.compact) .message.mentioned.first .markup:before, .chat .message-group.compact .message.mentioned.first .message-content:before 
{
    background:linear-gradient(40deg, transparent 43%, rgba(51, 234, 89,.45) 43%);
}
.chat .message-group:not(.compact) .message.mentioned .markup, .message-group.compact .message.mentioned .message-content 
{
    background-color:rgba(198, 156, 219,.3) !important; 
    text-shadow: 0 0 3px rgba(0,0,0,.2);
}
.app:not(.theme-light) .chat .message-group:not(.compact) .message.mentioned .markup .mention:not(:hover) 
{
    color: #acbeff; background-color: rgba(198, 156, 219,.07) !important;
}
.app:not(.theme-light) .chat .message-group:not(.compact) .message.mentioned .markup .mention:hover 
{
    color: #fff;
}
.chat .message-group .comment .markup code.inline, .chat .message-group .comment .markup pre code 
{
    background: rgba(20,20,20,.18) !important;
}
.chat .message .mention 
{
    border-radius: 2px;
}
.system-message 
{
    margin-top: 14px;
}
.system-message .system-message-icon, .system-message .system-message-content 
{
    margin-top: -2px;
}

.theme-dark .themed-popout .header,
.theme-dark .themed-popout{
    border-radius: 0;
    border: 0 !important;
    box-shadow: 0 0;
}

.theme-dark .themed-popout .header{
    background-color: rgba(0, 0, 0, 0)!important;
}

.theme-dark .themed-popout{
    background-color: rgba(0, 0, 0, 0.5)!important;
}


.theme-dark .messages-popout-wrap .messages-popout .message-group{
    background-color: rgba(0, 0, 0, 0)!important;
    border-radius: 0;
    border: 0 !important;
    box-shadow: 0 0;
}

.theme-dark .messages-popout-wrap .messages-popout .channel-separator .channel-name{
    color: rgba(255, 255, 255, 0.8)!important;
}


.channel-members .member:hover .status.status-online,
.channel-members .member.popout-open .status.status-online 
{
    border-color: #43b581 !important
}
.channel-members .member:hover .status.status-idle,
.channel-members .member.popout-open .status.status-idle 
{
    border-color: #faa61a !important
}
.channel-members .member:hover .status.status-dnd,
.channel-members .member.popout-open .status.status-dnd 
{
    border-color: #f04747 !important
}
.channel-members .member:hover .status.status-offline,
.channel-members .member.popout-open .status.status-offline 
{
    border-color: #636b75 !important
}
.channel-members .member:hover .status-streaming.status,
.channel-members .member.popout-open .status-streaming.status 
{
    border-color: #593695 !important
}
.channel-members .member.popout-open .status.status-typing,
.channel-members .member:hover .status.status-typing 
{
    background-color: rgba(0, 0, 0, 0.4) !important;
}
.channel-members .member.popout-open .status.status-typing .spinner .spinner-inner,
.channel-members .member:hover .status.status-typing .spinner .spinner-inner 
{
    left: 1px !important;top: 1px !important;
}
.channel-members .member.member-status-online .member-inner .member-activity-text strong 
{
    color: #43b581
}
.channel-members .member.member-status-idle .member-inner .member-activity-text strong 
{
    color: #faa61a
}
.channel-members .member.member-status-dnd .member-inner .member-activity-text strong 
{
    color: #f04747
}
.channel-members .member.member-status-offline .member-inner .member-activity-text strong 
{
    color: #636b75
}

/*channel list centered text*/

.channel-members h2, .private-channels header {
	text-align: center;
    color: rgba(198, 156, 219, 1) !important;
}

.channel-members h2::after, .private-channels header::after {
	content: "";
	display: block;
	height: 1px;
	width: 100%;
	border-bottom: 2px solid rgba(198, 156, 219, 1);
	padding-bottom: 10px;
}

/* underline labels in channellist */

.channels-3g2vYe .wrapperDefault-1Dl4SS, .channels-3g2vYe .wrapperHovered-1KDCyZ {
	padding-top: 28px;
	padding-right: 8px;
	padding-left: 18px;
	padding: 0 0 8px 0;
	height: auto;
	margin: 28px 8px 4px 18px;
	border-bottom: 2px solid rgba(198, 156, 219, 1);
}
.channels-3g2vYe .nameDefault-Lnjrwm, .channels-3g2vYe .nameHovered-1YFSWq {text-align: center;}
.channels-3g2vYe .wrapperDefault-1Dl4SS .flex-lFgbSz, .channels-3g2vYe .wrapperHovered-1KDCyZ .flex-lFgbSz {margin-right:0;}

/*custom voice avatar lights*/

.avatarSpeaking-3teDH6, .avatarDefault-3jtQoc, .avatarContainer-303pFz {
	border-color: transparent !important;
	box-shadow: none !important;
	z-index: 10;
}

.avatarSpeaking-3teDH6::before {
	content: "";
	position: absolute;
	left: -5px;
	width: 4px;
	height: 20px;
	background: rgb(198, 156, 219);
}

.avatarSpeaking-3teDH6::after {
	content: "";
	position: absolute;
	height: 20px;
	left: -5px;
	width: 100%;
	background: linear-gradient(to right, rgb(198, 156, 219) 0%, transparent 10%);
	opacity: 0.5;
	z-index: -1;
}

/*another try*/

.account .account-details .discriminator {visibility: hidden;}
.account .status {transform: translate(20px, -2px);}
.account .status.status-online:after,
.account .status.status-idle:after,
.account .status.status-dnd:after,
.account .status.status-invisible:after {
    color: #D2D2D2;
    font-size: 13px;
    position: absolute;
    left: 15px;
    top: -2px;
}
.account .status.status-online:after {content: "online";}
.account .status.status-idle:after {content: "idle";}
.account .status.status-dnd:after {content: "busy";}
.account .status.status-invisible:after {content: "invisible";}

