	body,
	.view-header-2,
	.view-header-2 .back,
	.view-header-2 .title,
	.view-header-2 .title .active,
	.view-header-2 .right-bar .bookshelf,
	.view-header-2 .right-bar .collection,
	.right-bar .read-style,
