	body>.full-class~.container,
	body>.full-class~div[class^="index-"],
	body>.full-class~section,
	body>.topFIxedMenu~.container,
	body>.topFIxedMenu~div[class^="index-"],
	body>.topFIxedMenu~section,
	body>.index-title,
	body>.index-title~.container,
	body>.index-title~div[class^="index-"],
	body>.index-title~section,
