	}
	
	.index-original-list li .cover {
		flex: 0 0 auto;
		margin-left: 1px
	}
	
	.index-original-list li .info {
		flex: 1 1 auto;
		padding: 0;
		margin: 0 2mm;
		overflow: hidden
	}
	
	.index-original-list li .info .tag {
		bottom: .5em
	}
	
	.index-original-list li .info .tag span {
		display: inline-block;
		padding: 1px 2pt;
		background: #ffd
	}
	
	.index-rank {
		padding: 0 0 0 20px
	}
	
	.index-rank-bar .right-list {
		width: calc((100% - 90px) / 5 - 32px)
	}
	
