	}
	
	.readPage {
		background: #777;
		padding: 3mm 0 0
	}
	
	#imgloading {
		color: transparent !important;
		margin: 0 !important
	}
	
	#imgloading img {
		margin-top: -9px !important;
		margin-left: -9px !important;
		filter: brightness(.2);
		zoom: 3
	}
	
	#cp_img {
		text-align: center
	}
	
	#cp_image,
	#cp_img>img {
		width: auto !important;
		max-width: calc(100% - 4mm) !important;
		height: calc(100vh - 12mm) !important;
		margin: 2mm auto !important;
		border-radius: 2mm;
		filter: brightness(.95) contrast(1.1)
	}
	
	.view-main,
	.view-main-1 {
		max-width: 100%;
		height: calc(100vh - 4mm);
		min-height: calc(100vh - 4mm) !important;
		box-sizing: border-box;
		margin-top: 0 !important;
		padding: 4mm 0 0
	}
	
	/* .actualSize 不調整圖片大小 */
	body.actualSize::-webkit-scrollbar,
	body.actualSize ::-webkit-scrollbar {
		max-width: 4mm;
		max-height: 4mm
	}
	
	body.actualSize::-webkit-scrollbar-thumb,
	body.actualSize ::-webkit-scrollbar-thumb {
		border-radius: 4mm;
		box-shadow: inset 0 0 0 1px #ec3
