	.carousel-control-prev,
	.carousel-control-next {
		top: 50%;
		bottom: unset;
		width: 30px;
		margin: -50px 0;
		background: rgba(255, 255, 255, .2);
		opacity: 1;
		transition: .3s
	}
	
	.carousel-control-prev:hover,
	.carousel-control-next:hover {
		background: rgba(255, 255, 255, .9);
		box-shadow: 0 0 1mm #777;
		opacity: 1
	}
	
	.carousel-control-prev-icon,
	.carousel-control-next-icon {
		filter: invert(.5)
	}
	
	.recommendRightTitle,
	.recommendRightContent,
	.rankingTime {
		position: static;
		width: auto;
		height: auto;
		margin: 0;
		line-height: 1.5
	}
	
	.item-rankingList {
		font-size: 1.2rem
	}
	
	.recommendRightTitle::before {
		content: "編輯推薦";
		display: block;
		width: 100%;
		background: #88e;
		font-size: 1.2rem;
		text-align: center;
		text-shadow: 0 1px 3px #338,
			0 -1px 3px #338,
			1px 0 3px #338,
			-1px 0 3px #338
	}
	
	.recommendRightContent {
		background: rgba(255, 255, 255, .4)
	}
	
	.recommendRightContentList {
		display: flex;
		min-width: 450px;
		padding: 2mm
	}
	
	.recommendRightContentList>a {
		flex: 1 1 30%;
		margin: 0 -1mm;
		padding: 2mm;
		border-radius: 2mm
	}
	
	.recommendRightContentList>a:hover {
		margin-top: -1px;
		background: #eee;
		box-shadow: 0 0 2mm 1px #23a;
		z-index: 2
	}
	
	.recommendRightContentItem,
	.recommendRightContentItemText {
		width: auto;
		max-width: none
	}
	
	.recommendRightContentItemImage {
		width: 100%;
		max-width: 188px;
		height: auto;
		margin: 0 auto
	}
	
	.recommendRightContentItem:hover .recommendRightContentItemImage {
		box-shadow: none
	}
	
	.recommendRightContentItemImage img {
		object-fit: cover
	}
	
	.cartoon-classify {
		padding: .5em 1em;
		background: #ddd
	}
	
	.cartoon-classify,
	.rankingTime {
		box-shadow: 0 0 0 1px #ccc
	}
	
	.comicRank-img {
		width: 150px;
		height: 200px
	}
	
	.comicRank-txt {
		width: calc(100% - 150px);
		padding: 2em .6em;
		background: #eef;
		border: none
	}
	
	.comicRank-all {
		padding: 2mm
	}
	
	#rankingListbox .tab-pane.fade>ul {
		padding: 1mm 2mm;
		border-radius: 3mm
	}
	
	#rankingListbox .tab-pane.fade>ul:nth-of-type(1) li {
		padding: 0;
		margin: 1mm 0;
		border-radius: 2mm
	}
	
	#rankingListbox .rank-icon {
		margin: 1mm
	}
	
	.rank-txt-box {
		flex: 1 1 auto
	}
	
	#rankingListbox p.rank-txt {
		display: block;
		width: auto;
		min-width: 10em;
		max-width: 16em;
		flex: 1 1 auto;
		padding: 0
	}
	
	#rankingListbox a.rank-txt {
		display: block;
		width: auto;
		max-width: none;
		padding: 1mm 0;
		text-overflow: ellipsis;
		overflow: hidden
	}
	
	.rank-icon,
	.ranking-all-icon,
	.comicRank-all-icon {
		flex: 0 0 auto;
		width: 1.6em;
		height: 1.6em;
		line-height: 1.6;
		margin-right: 1mm;
		border-radius: 1em;
		background: #669
	}
	
	.comicRank-all-txt {
		width: auto;
		flex: 1 1 auto
	}
	
	.comicRank-txt-span {
		flex: 0 0 auto;
		width: auto;
		max-width: 12em;
		color: #666
	}
	
	.specialContent {
		background: rgba(255, 255, 255, .7)
	}
	
	.specialContentTextContent {
		height: auto
	}
	
	.specialContentTextContent,
	.comicRank-txt-span a,
	.ranking-all-topThree-txt>span a {
		color: rgba(0, 0, 0, .5)
	}
	
	.comicParticulars-synopsis {
		padding: 3mm 1em !important;
		background: rgba(255, 255, 255, .5)
	}
	
	.comicParticulars-left-img {
		width: 100%;
		height: 100%;
		max-height: 100%
	}
	
	.comicParticulars-left-img img {
		object-fit: cover;
		object-position: 50% 10%
	}
	
	.correlationItem-img,
	.correlationItem-txt p {
		width: 100%;
		height: auto;
		max-height: 320px;
		overflow: hidden
	}
	
	.correlationItem-img a {
		overflow: hidden
	}
	
	.el-pagination.is-background .el-pager li {
		min-width: 1.4em;
		height: auto;
		line-height: 1.4;
		font-size: 1.2rem;
		border-radius: 4pt
	}
	
	@media screen and (max-width: 1200px) {
	
		html,
		body {
			width: 100vw;
			overflow-x: hidden;
		}
	
		.header-jum,
		.header-jum:hover {
			right: calc(100% - 100vw + 7mm);
		}
	
		.header-log {
			max-width: 100vw !important;
			margin: 0;
		}
	}
	
	@media screen and (max-width: 1220px) {
	
		.container.swiper-content,
		.container.recommend {
			flex-wrap: wrap
		}
	
		.swiperList {
			margin: 0 auto 2em
		}
	
		.swiper-rankingList,
		.recommendRight {
			margin: 2mm auto 0
		}
	
		.exemptComicList .col-10,
		.exemptComicList .col-2 {
			flex: 1 1 100%
		}
	}
	
	@media screen and (min-width: 1600pt) {
		.headerUpLoad span {
			font-size: 14pt
		}
	}
	
	@media screen and (min-width: 133vh) {
	
		.comicContent-list,
		.comicContent-image-list {
			max-width: min(160vh, calc(100vw - 15mm)) !important
		}
	
		body.actualSize .comicContent-list,
		body.actualSize .comicContent-image-list {
			max-width: none !important
		}
	
		.comicContent-list:hover>li:first-child {
			display: block
		}
	
		.comicContent-list {
			border-radius: 4mm;
			box-shadow: inset 0 0 0 1px #68c
		}
	
		.comicContent-list:hover {
			box-shadow: inset 0 0 8mm 1px #78c
		}
	}
}

