@-moz-document domain("manhua.dmzj.com"), domain("manhuagui.com"), domain("mhgui.com"), domain("copymanga.com"), domain("copymanga.info"), domain("copymanga.net"), domain("copymanga.org"), domain("copymanga.site") {
/*	共用圖片	*/
	
	@media screen and (min-width: 133vh) {
	
		#center_box::before,
		#tbBox #mangaMoreBox::before,
		.comicContent-list::before {
			position: fixed;
			top: 20%;
			right: max(calc(50vw - 76vh), 10mm);
			direction: ltr;
			font-size: 0;
			opacity: .8;
			background: rgba(0, 0, 0, .5);
			box-shadow: 0 0 1mm #eee;
			padding: 5px;
			border-radius: 5px;
			z-index: 2
		}
	
		/* 動漫之家 */
		body:not(.actualSize) #center_box::before,
		/* 漫畫櫃 */
		body:not(.actualSize) #tbBox #mangaMoreBox::before,
		/* 拷貝漫畫 */
		body:not(.actualSize) .comicContent-list::before {
			opacity: .2;
			animation: rearrangePulse-1 2s 1;
			content: url('data:image/png;base64,')
		}
	
		/* 動漫之家 */
		body:not(.actualSize) #center_box:hover::before,
		/* 漫畫櫃 */
		body:not(.actualSize) #tbBox:hover #mangaMoreBox::before,
		/* 拷貝漫畫 */
		body:not(.actualSize) .comicContent-list:hover::before {
			animation: rearrangePulse-2 2s 1;
			content: url('data:image/png;base64,')
		}
	}
	
	@keyframes rearrangePulse-1 {
		5% {
			opacity: 1
		}
	
		30% {
			opacity: 1
		}
	
		100% {
			opacity: .2
		}
	}
	
	@keyframes rearrangePulse-2 {
		5% {
			opacity: 1
		}
	
		30% {
			opacity: 1
		}
	
		100% {
			opacity: .2
		}
	}
}
