style.css:
html:before { content:\"\"; position:fixed; width:100%; height:100%; background-color:#fff; z-index:-9999999; }
html {
    background-color: #333;
    filter: grayscale(70%) contrast(80%) invert(100%);
    /*zoom: 110%;*/
    transform: scale(1.1);
    transform-origin: 0 0;
    width: 90.909%;
}

img {
    filter: invert(100%);
}
[style^="background-image:"] {
    filter: invert(100%);
}
@media screen and (-webkit-min-device-pixel-ratio:0)
{ 
    iframe {
        filter: invert(100%);
    } 
}
/* i really want this to be global */
@-moz-document url-prefix("https://podio.com") {
    .feature-tour-highlighted-parent.chat-pane.right-nav.expanded {
        transform: scale(0.89);
        transform-origin: 410px 0;
        width: 400px;
        height: 110%;
    }
}
