style.css:
@-moz-document domain('twitter.com') {
	
	/* Hide Left Sidebar */
	.dashboard-left {
      display: none;
    }
  
 	/* Hide Right Sidebar */
	.dashboard-right {
      display: none;
    }
  
  	/* Centre Feed */
  	#timeline {
      float: none;
      margin: 0 auto;
    }
  
}
