style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("digg.com") {

/* videos */
.v .news-digg {
  padding-right: 5px !important;
  margin-top: 5px !important;
}

.v .news-digg > .p-img > a,
.v .news-digg > .p-img > em {
  margin: -6px -103px 0 0 !important;
}

.v .news-digg > li:last-child {
  margin-top: 0 !important;
}

.v .news-digg > .digg-count > a {
  margin-top: -2px !important;
}

/* podcasts */
.p .news-digg {
  padding-right: 3px !important;
  margin-top: 5px !important;
}

.p .news-digg > .p-img > a,
.p .news-digg > .p-img > em {
  margin: -6px -90px 0 0 !important;
}

.p .news-digg > li:last-child {
  margin-top: -3px !important;
}

.p .news-digg > .digg-count > a {
  margin-top: -2px !important;
}

}
