style.css:
@-moz-document domain("searx.me"), domain("search404.io") {
/*======================
	BASE
======================*/


/*======================
	HEADER
======================*/

#q {
    height: 44px;
    border: none;
}
.input-group {
    border: 1px solid #DADADA;
    border-radius: 24px !important;
    transition: box-shadow 200ms cubic-bezier(0.4, 0.0, 0.2, 1);
    padding-left: 5px;
}

.input-group:hover,.input-group:active{
    box-shadow: 0 1px 6px 0 rgba(32,33,36,0.28);
    border-color: rgba(223,225,229,0);
}

input[type="search"]{
    background: transparent;
}

#search_form {
    margin-bottom: 30px;
    background: #fff;
    border-bottom: 1px solid #EBEBEB;
    padding-left: 140px !important;
}
#search_form .input-group-btn .btn {
    border: none
}
.input-group-btn button {
    height: 44px;
    background: transparent;
    color: #5583FF;
    padding: 0 22px 0 0;
    font-size: 16px;
}

#search_form .input-group-btn .btn:hover{
    background: transparent
}
.input-group-btn button .glyphicon-search{
    color: #5583FF;
    top: 2px;
}

#categories {
    margin-bottom: 0
}
#categories label {
    height: 15px;
    line-height: 15px;
    padding: 28px 16px 28px;
    border: none;
    flex-grow: 0;
    margin-bottom: 0;
    cursor: pointer;
    background: none;
}
.search_categories label:last-child,
#categories label:last-child,
.search_categories .input-group-addon:last-child,
#categories .input-group-addon:last-child {
    border: 0
}
.search_categories input[type=checkbox]:checked + label,
#categories input[type=checkbox]:checked + label {
    color: #29314d;
    font-weight: 700;
    border-bottom: #4285F4 3px solid;
}
.searx-navbar {
    background: #fff;
 
}
.searx-navbar .instance a,
.pull-right a {
    color: #000;
}

.searx-navbar .pull-left{display: none}


/*======================
	SEARCH RESULTS
======================*/

@media(min-width: 1200px){
    body > .container {
        margin-right: 0;
        margin-left: 0;
        padding: 0;
        width: 100% !important;
    }
}

.container .row{
    margin-left:0;
    margin-right:0;

}

#search_form .col-md-2 {padding: 6px}


#main_results{padding-left: 170px}

.result-default, .result-code, .result-torrent, .result-videos, .result-map{
    padding-left:0 !important;
    width: 632px;
}

.result_header .highlight {
    font-weight: normal;
}
.highlight,
.result-default:hover,
.result-code:hover,
.result-torrent:hover,
.result-videos:hover,
.result-map:hover {
    background: none !important;
}
#main_results {
    max-width: 632px;
    margin-right: calc(100% - 632px - 33.33333333%);
}
.result {
    display: flex;
    flex-flow: column wrap;
    margin-bottom: 30px;
}

.result .pull-right{
    margin-top: 3px;
}
.result .pull-right span:first-child{
    padding-left: 0;
}

.result .pull-right .text-info:last-child{
    padding-left: 10px;
}


h4.result_header a {
    font-size: 18px;
    order: 1;
    color: #1a0dab !important;
}
h4.result_header a:visited {
    color: #609 !important;
}
.text-muted {
    order: 2;
    color: #006621 !important;
    font-size: 14px;
    float: left;
    display: inline;
    position: relative;
}
.pull-right {
    order: 4;
}
.result-content {
    order: 3;
    margin-top: 4px;
}

.result-default .external-link{
    margin-bottom: 0
}

.result-default .external-link a{
    color: #069025 !important;
}

.input-group {
    width: 632px;
}
.favicon {
    display: none;
}
.external-link {
    font-size: 14px
}

/*======================
	SIDEBAR
======================*/

.infobox .btn {
    background: #4285F4;
    border-radius: 3px !important
}
.panel {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .16), 0 0 2px 0 rgba(0, 0, 0, .12)
}
.infobox p {
    font-family: sans-serif !important
}

/*======================
	Footer
======================*/

.footer .container .text-muted{
    float: none;
    margin: 0 auto;
    padding-bottom: 20px
}

/*======================
	MEDIA QUERIES
======================*/

@media (min-width: 1200px) {
    
    #search_form {
        padding-top: 20px;
    	padding-left: 130px;
    	padding-right: 25px;
    }
    #search_form:before {
        width: 80px;
        height: 45px;
        content: " ";
        background: url(https://searx.me/static/themes/oscar/img/logo_searx_a.png) no-repeat;
        background-size: contain;
        position: absolute;
        left: 40px;
    	top: 57px;
    }
}
}
