style.css:
@-moz-document domain(radio.yandex.ru) {
body, .button_round.button_checked, .notify-fancy__message, .notify-fancy__spinner,
 .track .track__menu, .nav__shim, .slider__item .slider__more-label
{
    filter: invert(90%);
}
.track .track__cover, .nb-user-avatar{
    filter: invert(100%);
}
.button_round.button_checked, .button_round.button_checked:hover {
    background: #fff;
}
.notify-fancy__message, .notify-fancy__spinner  {
    background-color: #fff;
}
/*rounded track icons*/
.track .track__icon, .track .track__cover {
    border-radius: 50%;
}
.track__shim {
    border-radius: 50%;
}
}
