	margin-bottom: 0px;
}
/* description */
#js-app .HeaderContainer > .VideoDescriptionContainer {
	order: -1;
	margin-top: 42px;
	margin-bottom: 25px;
	width: var(--cajHeaderItemsWidthNormal);
}
