	width: auto;
	max-width: var(--cajHeaderItemsWidthNormal);
}
#js-app .HeaderContainer > .OwnerNicoliveBannerContainer:not(:empty) {
	margin-top: 10px;
	margin-bottom: 30px;
}



/* *** layout adjust to browser-width */

@media (max-width: 943px) {
	/* header all width */
	body.is-large #js-app .HeaderContainer > :first-child.HeaderContainer-row,
	body.is-large #js-app .HeaderContainer > .TagContainer,
	body.is-large #js-app .HeaderContainer > .VideoDescriptionContainer,
	body.is-large #js-app .HeaderContainer > .OwnerNicoliveBannerContainer {
		width: 854px !important;
	}
	body:not(.is-large) #js-app .HeaderContainer > :first-child.HeaderContainer-row,
	body:not(.is-large) #js-app .HeaderContainer > .TagContainer,
	body:not(.is-large) #js-app .HeaderContainer > .VideoDescriptionContainer,
	body:not(.is-large) #js-app .HeaderContainer > .OwnerNicoliveBannerContainer {
		width: 640px !important;
	}
	/* video-title, search-box */
	#js-app .HeaderContainer > :first-child.HeaderContainer-row .GridCell {
		display: block;
	}
	#js-app .HeaderContainer > :first-child.HeaderContainer-row .GridCell.HeaderContainer-videoTitle {
		padding-right: 0;
		margin-bottom: 12px;
	}
	#js-app .HeaderContainer > :first-child.HeaderContainer-row .GridCell.HeaderContainer-searchBox {
		width: 100%;
	}
	#js-app .HeaderContainer > :first-child.HeaderContainer-row .GridCell.HeaderContainer-searchBox > .SearchBox {
		margin-left: auto;
		margin-right: 0;
	}
	/* nushi, score */
	body:not(.is-large) #js-app .HeaderContainer > .VideoDescriptionContainer + .HeaderContainer-row > .GridCell {
		display: block;
	}
	body:not(.is-large) #js-app .VideoOwnerInfo-links {
		max-width: 420px;
	}
	body:not(.is-large) #js-app .VideoUploadDateMeta,
	body:not(.is-large) #js-app .VideoViewCountMeta,
	body:not(.is-large) #js-app .CommentCountMeta,
	body:not(.is-large) #js-app .MylistCountMeta,
	body:not(.is-large) #js-app .CategoryRankMeta {
		min-width: fit-content;
	}
}



