/* **** edit mode */
#js-app li.TagItem {
	transition: 0.5s;
	counter-increment: caj_tag_i;
}
#js-app li.TagItem.is-category {
	counter-increment: none;
}
#js-app li.TagItem.is-editing {
	border-radius: 0px 16px 16px 0px;
	background-color: var(--cajTagEditTagitemBackgroundColor);
	transform: rotate(-2deg);
	position: relative;
	top: 2px;
}
#js-app li.TagItem.is-editing .TagItem-name {
	color: var(--cajTagEditTagitemTextColor);
}
#js-app li.TagItem.is-editing.is-locked {
	border-radius: 4px;
	background-color: var(--cajTagEditTagitemLockedBackgroundColor);
	transform: none;
	top: 0px;
}
#js-app li.TagItem.is-editing::before {
	content: counter(caj_tag_i);
	display: inline-block;
	position: absolute;
	top: 2px;
	left: 2px;
	font-size: 11px;
	line-height: 1em;
	font-weight: bold;
	color: var(--cajTagEditTagitemIndexTextColor);
}
#js-app li.TagItem.is-editing.is-category::before {
	content: "";
}
